/// <summary>
///     Overrides the jumpref method so that the company is changed upon "View Detail", as the <c>ForecastItemAllocation</c>
///     Calls the table level method.
/// </summary>
public void jumpRef()
{
    DataAreaId curComp = curext();
    reqDemPlanForecastIAKMap.forecastAllocationKeyJumpRef();
    super();
    appl.setDefaultCompany(curComp, false);
}

