[Form]
public class ReqIncomingIntercompanyDemand extends FormRun
{
    ReqIntercompanyConversionUtil   conversionUtil;
    ReqIntercompanyDemandPlanFilter intercompanyDemandPlanFilter;
    dataAreaId companyIdWhereFormWasOpened;

    // Used to initialize the filter and query when caller record is a ReqTrans with IntercompanyPlannedOrder.
    ReqTrans callerReqTrans;
}

public void activate(boolean _active)
{
    super(_active);

    if (_active)
    {
        this.changeToCompanyWhereFormWasOpened();
    }
}

private void changeToCompanyWhereFormWasOpened()
{
    if (curext() != companyIdWhereFormWasOpened)
    {
        appl.setDefaultCompany(companyIdWhereFormWasOpened, true);
    }
}

private EcoResProductMaster findProductMaster()
{
    EcoResDistinctProductVariant productVariant = EcoResDistinctProductVariant::find(reqPoCust.Product);
    return EcoResProductMaster::find(productVariant.ProductMaster);
}

public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    super();

    intercompanyDemandPlanFilter = ReqIntercompanyDemandPlanFilter::construct();

    conversionUtil = ReqIntercompanyConversionUtil::construct();

    companyIdWhereFormWasOpened = curext();

    this.initializeCaller();

    this.initializePlanFilter();

    this.initializeQuery();
}

/// <summary>
/// Initializes the caller member variable.
/// </summary>
private void initializeCaller()
{
    if (this.args().dataset() == tableNum(ReqTrans))
    {
        callerReqTrans = this.args().record();
    }
}

/// <summary>
/// Initializes the plan filter from the caller or the default dynamic plan.
/// </summary>
private void initializePlanFilter()
{
    ReqPlanId planId;

    if (callerReqTrans && callerReqTrans.IntercompanyPlannedOrder)
    {
        planId = ReqPlanVersion::find(callerReqTrans.PlanVersion).ReqPlanId;
    }
    else
    {
        var reqPlanIdArgs = ReqTransPlanIdFilter::getPlanIdFromArgsSysFilter(this.args());
        if (reqPlanIdArgs)
        {
            planId = reqPlanIdArgs;
        }
        else
        {
            planId = ReqPlanSched::defaultDynamicId();
        }
    }

    planFilter.text(planId);
}

/// <summary>
/// Initializes the query and applies ranges.
/// </summary>
/// <param name="_reQuery">A boolean variable determining whether to re-execute the query due to filter changes.</param>
public void initializeQuery(boolean _reQuery = false)
{
    // When requery then the filtering has changed and we should not filter on the caller anymore.
    if (_reQuery)
    {
        callerReqTrans = null;
    }

    var planningGroupRecId = ReqIntercompanyPlanningGroup::findByName(InterCompanyPlanningGroupFilter.text()).RecId;
    var planVersions = intercompanyDemandPlanFilter.getPlanVersions(planFilter.text(), planningGroupRecId, callerReqTrans);

    var queryFactory = ReqIntercompanyQueryFactory::construct();
    reqPOCust_ds.query(queryFactory.getIncomingDemandQuery(planVersions, true, '', callerReqTrans).query());

    if (_reQuery)
    {
        reqPOCust_ds.executeQuery();
    }
}

public void jumpRefToUnitOfMeasure()
{
    UnitOfMeasure unitOfMeasure = UnitOfMeasure::findBySymbol(conversionUtil.getCustomerInventUnitOfMeasure(reqPoCust));
    this.openMenuItem(menuitemDisplayStr(UnitOfMeasure), MenuItemType::Display, unitOfMeasure);
}

private void openMenuItem(menuItemName _menuItemName, MenuItemType _menuItemType, Common _record = reqPoCust)
{
    Args args;
    MenuFunction menuFunction;

    args = new Args();
    args.caller(this);
    args.record(_record);

    menuFunction = new MenuFunction(_menuItemName, _menuItemType);
    menuFunction.run(args);
}

protected FormControl findProductDimControl(InventProductDimension _productDim)
{
    // Find the product dimension control based on the product dimension field name
    int ctrlId = element.controlId(fieldId2Name(tableNum(InventDim), _productDim.parmInventDimFieldId()));
    if (ctrlId)
    {
        return element.control(ctrlId);
    }
    return null;
}

private void setProductDimControlValue(FormControl _productDimControl, anytype _productDimValue)
{
    switch (true)
    {
        case _productDimControl is FormStringControl:
            FormStringControl dimStrCtrl = _productDimControl as FormStringControl;
            dimStrCtrl.text(_productDimValue);
            break;
        case _productDimControl is FormDateTimeControl:
            FormDateTimeControl dimDateTimeCtrl = _productDimControl as FormDateTimeControl;
            dimDateTimeCtrl.dateTimeValue(_productDimValue);
            break;
        case _productDimControl is FormRealControl:
            FormRealControl dimRealCtrl = _productDimControl as FormRealControl;
            dimRealCtrl.realValue(_productDimValue);
            break;
        default:
            throw error(Error::wrongUseOfFunction(funcName()));
    }
}

public void updateDemandDetailsDisplay()
{
    inventSiteIdVendor.text(conversionUtil.convertSiteId(reqPOCust));

    inventlocationIdVendor.text('');
    inventlocationIdVendor.enabled(conversionUtil.mustConvertWarehouseId(reqPOCust));
    if (inventlocationIdVendor.enabled())
    {
        inventlocationIdVendor.text(conversionUtil.convertWarehouseId(reqPOCust));
    }

    Enumerator productDims = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (productDims.moveNext())
    {
        InventProductDimension productDim = productDims.current();
        FieldId productDimFieldId = productDim.parmInventDimFieldId();
        FormControl dimensionCtrl = element.findProductDimControl(productDim);
        if (dimensionCtrl)
        {
            element.setProductDimControlValue(dimensionCtrl, InventDim::emptyDimField(productDimFieldId));
            dimensionCtrl.enabled(conversionUtil.isDimensionActive(ReqPOCust, productDimFieldId));
            if (dimensionCtrl.enabled())
            {
                element.setProductDimControlValue(dimensionCtrl, conversionUtil.getDimensionValue(ReqPOCust, productDimFieldId));
            }
        }
    }
}

private void cacheDisplayMethods()
{
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyReqPlanId));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyConvertedItemId));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyConvertedQty));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyConvertedReqDate));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyInventLocationId));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanySiteId));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyInventUnitId));
    this.cacheAddMethod(tablemethodstr(ReqPO, intercompanyLegalEntityDescription));
}

public int active()
{
    int ret;

    ret = super();

    element.updateDemandDetailsDisplay();

    return ret;
}

public void clicked()
{
    Args args;

    args = new Args();
    args.record(InventTable::find(reqPOCust.intercompanyConvertedItemId()));
    args.parm(planFilter.text());
    args.caller(element);

    this.menufunction().run(args);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.initializeQuery(true);
    }

    return ret;
}

