void doResearch()
{
    reqItemTable_ds.research();
}

void enableFields()
{
    if (!#PMFEnabled)
    {
        reqItemTable_ds.object(fieldnum(ReqItemTable, MaxInventOnhand)).allowEdit(reqItemTable.isMaxInventOnhandEditable());
    }

    reqItemTable_ds.object(fieldnum(ReqItemTable, MaxSafetyKeyId)).enabled(reqItemTable.isMaxSafetyKeyIdEditable());
    reqItemTable_ds.object(fieldnum(ReqItemTable, CovPeriod)).allowEdit(reqItemTable.CovRule == ReqCovType::Period ? true : false);
    reqItemTable_ds.object(fieldnum(ReqItemTable, MinSafetyKeyId)).enabled(reqItemTable.isMinSafetyKeyIdEditable());
    reqItemTable_ds.object(fieldnum(ReqItemTable, MinSafetyPeriod)).enabled(reqItemTable.isMinSafetyPeriodEditable());
    reqItemTable_ds.object(fieldnum(ReqItemTable, InventLocationIdReqMain)).allowEdit(reqItemTable.ReqPOTypeActive && reqItemTable.ReqPOType == ReqPOType::Transfer);
    reqItemTable_ds.object(fieldnum(ReqItemTable, ReqGroupId)).allowEdit(reqItemTable.ItemCovFieldsActive);

    if (pmfFormCtrl_ReqItemTable)
    {
        // active will not be called when init the form
        pmfFormCtrl_ReqItemTable.setReqItemTableBuffer(reqItemTable);
        pmfFormCtrl_ReqItemTable.refresh();
    }

    ctrlOnHandFields.caption("@SCM:ReqPeggingSequenceOverrideLabel");
}

/// <summary>
/// Gets the buffer used as context when opening the <c>DocuView</c> form.
/// </summary>
/// <returns>The document context buffer.</returns>
/// <remarks>
/// This method is overridden to ensure that the document context is set to the item coverage
/// even if the selected control is bound to an inventory dimension.
/// </remarks>
public Common docCursor()
{
    Common docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = reqItemTable;
    }

    return docCursor;
}

protected boolean hasDimensionsChanged()
{
    InventDim   inventDim_orig;

    inventDim_orig.data(inventDim.orig());
    return ! inventDim.equal(inventDim_orig);
}

public void init()
{
    boolean isReqItemTableMinMaxPeriodFlightEnabled = ReqItemTableMinMaxPeriodFlight::instance().isEnabled();

    ReqParameters::checkPlanningProcessesAreEnabled();

    if (!this.validateInventTable())
    {
        throw error(strfmt("@SYS27080", element.name()));
    }

    super();

    if (this.isProcessManufacturingEnabled())
    {
        pmfFormCtrl_ReqItemTable =
            PmfFormCtrl_ReqItemTable::newFromForm(element);
        pmfFormCtrl_ReqItemTable.setCaller(element.inventTable());
        pmfFormCtrl_ReqItemTable.setDatasources(reqItemTable);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    if (element.args().caller() && element.args().caller().name() == formstr(ReqItemTableSetup))
    {
        wizardButton.visible(false);
    }

    if (pmfFormCtrl_ReqItemTable)
    {
        // active will not be called when init the form
        pmfFormCtrl_ReqItemTable.setReqItemTableBuffer(reqItemTable);
        pmfFormCtrl_ReqItemTable.initPost();
    }

    if (isReqItemTableMinMaxPeriodFlightEnabled)
    {
        MinMaxPeriods.visible(false);
    }

    MinMaxPeriodsV2.visible(isReqItemTableMinMaxPeriodFlightEnabled);
}

