void initCaption()
{
    str                                     captionText = tableId2pname(tableNum(ReqItemTable));
    InventDimGroupFieldSetupEnumerator      enumerator;
    InventDimGroupFieldSetup                inventDimGroupFieldSetup;
    fieldId                                 inventDimFieldId;

    if (reqItemTable.CovInventDimId && inventDimGroupSetup)
    {
        enumerator = inventDimGroupSetup.getFieldSetupEnumerator();
        while (enumerator.moveNext())
        {
            inventDimGroupFieldSetup = enumerator.current();

            if (inventDimGroupFieldSetup.isActive() && inventDimGroupFieldSetup.isCoveragePlanByDimensionEnabled())
            {
                inventDimFieldId = inventDimGroupFieldSetup.dimFieldId();
                if (inventDim.(inventDimFieldId))
                {
                    captionText += ', '+fieldId2pname(tableNum(InventDim), inventDimFieldId)+': '+inventDim.(inventDimFieldId);
                }
            }
        }
    }

    element.design().caption(captionText);
}

