InventTable inventTable(boolean _reread = false)
{
    Common      common;
    ItemId      itemId;
    FieldName   fieldName;
    TableId     tableId;

    if (_reread || !inventTableCache)
    {
        inventTableCache.clear();

        if (   element.args()
            && element.args().record())
        {
            common = element.args().record();
            tableId = common.TableId;

            if (tableId == tableName2Id(tableStr(InventTable)))
            {
                fieldName = fieldStr(InventTable, ItemId);
            }
            else
            {
                FieldId fieldId = this.getItemIdEDTFieldId(tableId);

                if (fieldId)
                {
                    fieldName = fieldId2Name(tableId, fieldId);
                }
            }

            if (fieldName)
            {
                itemId = common.(fieldName2id(tableId, fieldName));
            }

            if (itemId)
            {
                inventTableCache = InventTable::find(itemId);
            }
        }
    }

    return inventTableCache;
}

private FieldId getItemIdEDTFieldId(TableId _tableId)
{
    const ExtendedTypeId itemIdEDTId = extendedTypeNum(ItemId);

    FieldId fieldId     = 0;
    var     dictTable   = new DictTable(_tableId);

    for (int field = 1; field <= dictTable.fieldCnt(); field++)
    {
        fieldId = dictTable.fieldCnt2Id(field);
        var dictField = new DictField(_tableId, fieldId);

        if (dictField.typeId() == itemIdEDTId)
        {
            break;
        }
    }

    return fieldId;
}

/// <summary>
///    Creates a <c>ReqSetupDim</c> object using the given parameters or retrieves it from the cache map.
/// </summary>
/// <param name="_reqItemTable">
///    A <c>ReqItemTable</c> record.
/// </param>
/// <param name="_inventDim">
///    A <c>InventDim</c> record.
/// </param>
/// <param name="_setCovInventDimId">
///    A Boolean value that indicates if the <c>InventDim</c> datasource can be set.
/// </param>
/// <param name="_reread">
///    A Boolean value that indicates if the <c>ReqSetupDim</c> object can be read from the cache map.
/// </param>
/// <returns>
///    A <c>ReqSetupDim</c> object.
/// </returns>
public ReqSetupDim reqSetupDim(
    ReqItemTable    _reqItemTable      = reqItemTable,
    InventDim       _inventDim         = inventDim,
    boolean         _setCovInventDimId = true,
    boolean         _reread            = false)
{
    InventDim           inventDimNew;
    str                 dimKey;
    ReqSetupDimForm     reqSetupDimForm;

    if (   _setCovInventDimId
        && (!_reqItemTable.CovInventDimId
        ||  element.hasDimensionsChanged()))
    {
        inventDimNew = InventDim::findOrCreate(inventDim);
        _reqItemTable.CovInventDimId = inventDimNew.InventDimId;
        inventDim.data(inventDimNew);
        inventDim_ds.setCurrent();
    }

    if (   !reqSetup
        || reqSetup.itemId() != _reqItemTable.ItemId
        || reqSetup.inventTable().ReqGroupId != inventTableCache.ReqGroupId)
    {
        reqSetup = ReqSetup::newItemId(_reqItemTable.ItemId);
        this.initMapCache();
    }

    if (!mapReqSetupDimForm)
    {
        this.initMapCache();
    }

    dimKey = _inventDim.mapKeyCov();

    if (!mapReqSetupDimForm.exists(dimKey) || _reread)
    {
        reqSetupDimForm = ReqSetupDimForm::newfromForm(reqSetup,_reqItemTable);
        mapReqSetupDimForm.insert(dimKey, reqSetupDimForm);
        reqSetupDimForm.showInfolog();
    }
    else
    {
        reqSetupDimForm = mapReqSetupDimForm.lookup(dimKey);
    }

    reqSetupDimForm.update(_reqItemTable);
    return reqSetupDimForm;
}

