}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_ReqItemTable::newFromForm(element);
            }
        case InventDimFormDesignUpdate::Active      :
        case InventDimFormDesignUpdate::LinkActive  :

            if (inventDimGroupSetup)
            {
                inventDimParm.initCovReqItemTable(inventDimGroupSetup);

                inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
                inventDimFormSetup.parmDimParmEnabled(inventDimParm);
            }

        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.parmMarkFieldAsMandatory(reqItemTable.MinInventOnhand > 0 ? true : false);
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
///    Removes outdated entries from the cache map.
/// </summary>
public void updateMapReqSetupDim()
{
    str             dimKey;
    int             loop;
    Set             keySet;
    Set             keySetToRemove;
    SetEnumerator   se;

    dimKey         = inventDim.mapKeyCov();
    keySetToRemove = new Set(mapReqSetupDimForm.keyType());
    keySet         = mapReqSetupDimForm.keySet();

    if (!dimKey)
    {
        // blank coverage dimension removes all cache entries
        this.initMapCache();

        // execute query to ensure fresh data is shown
        reqItemTable_ds.research(true);
    }
    else
    {
        if (   keySet
            && keySet.elements())
        {
            // build a set of keys to remove from the map
            do
            {
                loop++;

                se     = keySet.getEnumerator();
                while (se.moveNext())
                {
                    if (   se.current() == dimKey
                        || strScan(se.current(),dimKey,1,strLen(se.current())))
                    {
                        keySetToRemove.add(se.current());
                    }
                }

                if (element.hasDimensionsChanged() && loop == 1)
                {
                    dimKey = inventDim.orig().mapKeyCov();
                }
                else
                {
                    loop++;
                }
            }
            while (loop <= 1);

            // iterate the set and remove corresponding elements from the map
            if (keySetToRemove.elements())
            {
                se = keySetToRemove.getEnumerator();
                while (se.moveNext())
                {
                    mapReqSetupDimForm.remove(se.current());
                }
            }
        }
    }
}

