/// <summary>
/// Determine whether to create an instance of the <c>InventDimGroupSetup</c> class for a specific form caller.
/// </summary>
/// <returns>true if we should create an instance, otherwise false</returns>
public boolean mustInitInventDimGroupSetupFromCallerOnLinkActive()
{
    switch (element.args().caller().name())
    {
        case formstr(EcoResProductDetailsExtended):
        case formstr(EcoResProductPerCompanyListPage):
        case formStr(EcoResProductInformationDialog):
        case formstr(ReqItemTableSetup):
        case formstr(EcoResItemsProdTypeBOMExpiringBOMFormPart):
        case formstr(EcoResItemsProdTypeBOMExpiringRouteFormPart):
        case formstr(EcoResItemsMissingActiveBOMVersionFormPart):
        case formstr(EcoResItemsMissingActiveRouteVersionFormPart):
        case formstr(EcoResProductProdTypeFormulaNoActiveFormulaFormPart):
        case formstr(EcoResProductProdTypeFormulaNoActiveRouteFormPart):
        case formstr(ReqTransOverview):
        case formstr(ReqTransFuturesListPage):
            return true;
    }

    return false;
}

