public void write()
{
    if (!ReqItemTable.RecId || ReqItemTable.anyNonInheritedFieldValuesChanged())
    {
        ttsbegin;

        reqItemTable.CovInventDimId = InventDim::findOrCreate(inventDim).InventDimId;

        super();

        if (reqItemTable.CovInventDimId != inventDim.InventDimId)
        {
            inventDim.data(InventDim::find(reqItemTable.CovInventDimId));
            inventDim_ds.setCurrent();
        }

        ttscommit;

        element.updateMapReqSetupDim();
    }
}

public void linkActive()
{
    Query                   query;
    QueryBuildDataSource    qbds;
    QueryBuildDataSource    qbdsDim;
    QueryBuildRange         qbr;
    InventDim               inventDimCaller;
    ReqTrans                reqTransCaller;

    List                    sortedCovDimFields;
    ListEnumerator          enumerator;
    fieldId                 dimFieldId;

    ReqItemTable            positionAfterExecute;

    if (element.mustInitInventDimGroupSetupFromCallerOnLinkActive())
    {
        inventDimGroupSetup = InventDimGroupSetup::newInventTable(element.inventTable(true));
    }

    switch (element.args().caller().name())
    {
        case formstr(ReqTransOverview):
        case formstr(ReqTransFuturesListPage):
            query = this.query();
            if (query)
            {
                qbds = query.dataSourceTable(tablenum(ReqItemTable));
                qbr = SysQuery::findOrCreateRange(qbds,fieldnum(ReqItemTable,ItemId));
                qbr.value(queryValue(element.inventTable().ItemId));
            }

            if (element.args().record().TableId == tablenum(ReqTrans))
            {
                reqTransCaller = element.args().record() as ReqTrans;
                inventDimCaller = InventDim::find(reqTransCaller.CovInventDimId);

                qbdsDim = query.dataSourceTable(tablenum(InventDim));
                qbdsDim.clearRanges();

                sortedCovDimFields = InventDimMetaDataCache::dimCovDimEnabledFieldList();
                enumerator = sortedCovDimFields.getEnumerator();
                while (enumerator.moveNext())
                {
                    dimFieldId = enumerator.current();

                    if (inventDimCaller.(dimFieldId))
                    {
                        qbdsDim.addRange(dimFieldId).value(queryValue(inventDimCaller.(dimFieldId)));
                    }
                    qbdsDim.addRange(dimFieldId).value(SysQuery::valueEmptyString());  // in order to secure that invalid records aren't selected and default records are selected
                }
            }

            this.query(query);                    
            positionAfterExecute = ReqItemTable::find(element.inventTable().ItemId, reqTransCaller.CovInventDimId);
            break;
    }

    super();

    if (positionAfterExecute)
    {
        reqItemTable_ds.positionToRecord(positionAfterExecute);
    }

    element.updateDesign(InventDimFormDesignUpdate::LinkActive);
}

public void executeQuery()
{
    ReqItemTable    reqItemTableLocal;

    super();

    if (!element.mustLoadAllRows())
    {
        // Make sure all records are loaded in memory before we start calling initFromReqSetupDim to ensure we're not skipping any records due to paging.
        while(!this.allRowsLoaded())
        {
            this.next();
        }
    }

    this.setPosition(1);

    reqItemTableLocal = reqItemTable_ds.getFirst(0,false);

    if (!element.mustLoadAllRows() && reqItemTableLocal && mapReqSetupDimForm && element.args().caller() && element.args().caller().name() == formStr(EcoResProductDetailsExtended))
    {
        // Coverage group of inventTable may be changed after item coverage form is opened, so need to reresh inventTable cache to reflect the latest value
        element.inventTable(true);
    }

    while (reqItemTableLocal)
    {
        reqItemTableLocal.initFromReqSetupDim(element.reqSetupDim(reqItemTableLocal,reqItemTableLocal.inventDim(),false));
        reqItemTableLocal = reqItemTable_ds.getNext();
    }

}

