public void init()
{
    Query                   query;
    QueryBuildDataSource    qbdsDim;

    List                    sortedCovDimFields;
    ListEnumerator          enumerator;

    super();

    query = this.query();
    if (query)
    {
        qbdsDim = query.dataSourceTable(tablenum(InventDim));

        sortedCovDimFields = InventDimMetaDataCache::dimCovDimEnabledFieldList();
        enumerator = sortedCovDimFields.getEnumerator();
        while (enumerator.moveNext())
        {
            qbdsDim.addSortField(enumerator.current(), SortOrder::Ascending);
        }
    }
}

