void clicked()
{
    if (#PMFEnabled && !reqItemTable.RecId)
    {
        reqItemTable.initValue();
    }

    super();
}

void clicked()
{
    InventDim       inventDimDefault;
    ReqItemTable    reqItemTableDefault;

    super();

    inventDimDefault.data(inventDim);

    while ( inventDimDefault.clearOneCovDim() && ! reqItemTableDefault.RecId)
    {
        inventDimDefault = InventDim::findOrCreate(inventDimDefault);
        reqItemTableDefault = ReqItemTable::find(reqItemTable.ItemId,inventDimDefault.InventDimId);
    }

    if (reqItemTableDefault.RecId)
    {
        reqItemTable_ds.positionToRecord(reqItemTableDefault);
    }
}

public void pageActivated()
{
    InventDim   inventDim_orig;

    inventDim_orig.data(inventDim.orig());

    if (! inventDim.equal(inventDim_orig))
    {
        element.initValueReqItemTable();
    }

    super();
}

