public class ReqMRPParametersSetupWizard extends FormRun
{
    ReqMRPParametersSetupWizard sysWizard;
    private const int PlanOptimizationPageNumber = 2;
    private const int PlanScopePageNumber = 3;
    private const int PlanSchedulingPageNumber = 4;
    private const int PlanDemandUpdatesPageNumber = 5;
    private boolean showAll = false;

}

public void init()
{
    super();

    if (element.args().caller())
    {    
        sysWizard = element.args().caller();
    }

}

public SysWizard wizard()
{
    return sysWizard;
}

private void doActiveFields()
{
    // display groups that are enabled
    if (reqplanSched)
    {
        this.setUpCheckboxes();
        ReqMRPSetupWizardParameters.MaxBomLevel = 1;

        str legalEntityName = CompanyInfo::findDataArea(curExt()).name();
        str dataArea = CompanyInfo::findDataArea(curExt()).DataArea;

        if (reqplanSched.Name)
        {

            WizardExplanation.text(strFmt("@SCMPlanning:ReqMRPIntroPlanDetails", reqplanSched.ReqPlanIdSched, reqplanSched.Name, dataArea, legalEntityName));
            MasterPlanReview.text(strFmt("@SCMPlanning:ReqMRPMasterPlanReviewDetail", reqplanSched.ReqPlanIdSched, reqplanSched.Name));
        }
        else
        {
            WizardExplanation.text(strFmt("@SCMPlanning:ReqMRPintro", reqplanSched.ReqPlanIdSched, dataArea, legalEntityName));
            MasterPlanReview.text(strFmt("@SCMPlanning:ReqMRPMasterPlanReview", reqplanSched.ReqPlanIdSched));
        }

        MasterPlanningParametersReview.text(strFmt("@SCMPlanning:ReqMRPMasterPlanningParametersReview", dataArea, legalEntityName));
    }
    this.doManufacturingFields();
}

private void doManufacturingFields()
{
    sysWizard.skipTab(PlanSchedulingPageNumber, !ManufacturingQuestionCheckBox.value());
    BomLevels.enabled(ManufacturingQuestionCheckBox.value());
}

private void setUpCheckboxes()
{
    ReqPlanSched.TimeFenceCovFixed = NoYes::Yes;
    ReqMRPSetupWizardParameters.IsFreezeActive           = this.getAndSetupTimeFenceLockingValue();
    ReqMRPSetupWizardParameters.IsForecastPlanActive     = this.getAndSetupTimeFenceMasterPlanValue();
    ReqMRPSetupWizardParameters.IsCapacityActive         = this.getAndSetupTimeFenceCapacityValue();
    ReqMRPSetupWizardParameters.IsFirmingActive          = this.getAndSetupTimeFenceAuthorizationValue();
    ReqMRPSetupWizardParameters.IsRequisitionsActive     = this.getAndSetupTimeFenceBackRequisitionValue();
    ReqMRPSetupWizardParameters.IsSequencingActive       = this.getAndSetupPMFSeqTimeFenceSequencingValue();
    ReqMRPSetupWizardParameters.IsActionMessageActive    = this.getAndSetupTimeFenceActionValue();
    ReqMRPSetupWizardParameters.IsCalculatedDelaysActive = this.getAndSetupTimeFenceFuturesValue();
    ReqMRPSetupWizardParameters.IsCoverageOverriden      = this.getAndSetupTimeFenceCoverageValue();
    ReqMRPSetupWizardParameters.IsExplosionActive        = this.getAndSetupTimeFenceExplosionValue();
    ReqMRPSetupWizardParameters.IsJobSchedulingActive    = ReqPlanSched.CovSchedMethod == ProdSchedMethod::JobScheduling;
}

private ReqNoYesOverrideCombo setFieldAndGetValue(str _booleanField, str _timeFenceField, FormGroupCOntrol _group)
{

    if (!ReqPlanSched.getFieldValue(_booleanField))
    {
        _group.enabled(false);
        return ReqNoYesOverrideCombo::YesCoverageGroup;
    }
    if (ReqPlanSched.getFieldValue(_timeFenceField))
    {
        _group.enabled(true);
        return ReqNoYesOverrideCombo::YesOverride;
    }
    _group.enabled(false);
    return ReqNoYesOverrideCombo::No;
}

private boolean displayChangeReqPlanSched(str _booleanField, str _timeFenceField)
{
    return this.showAll || (ReqPlanSched.getFieldValue(_booleanField)!= ReqPlanSchedOrig.getFieldValue(_booleanField)
        || ReqPlanSched.getFieldValue(_timeFenceField) != ReqPlanSchedOrig.getFieldValue(_timeFenceField));
}

private boolean displayChangeReqParameters(str _field)
{
    return this.showAll || ReqParameters.getFieldValue(_field) != ReqParametersOrig.getFieldValue(_field);
}

private void setGroupAndFieldFromValue(str _comboBoxValue, str _booleanField, str _timeFenceField, FormGroupControl _group)
{
    switch(_comboBoxValue)
    {
        case "@SCMPlanning:ReqMRPYesOverride":
            {
                ReqPlanSched.setFieldValue(_booleanField, NoYes::Yes);
                _group.enabled(true);
            }
            break;
        case "@SCMPlanning:ReqMRPYesFromCoverageGroup":
            {
                ReqPlanSched.setFieldValue(_booleanField, NoYes::No);
                ReqPlanSched.setFieldValue(_timeFenceField, 0);
                _group.enabled(false);
            }
            break;
        case "@SYS2048":
            {
                ReqPlanSched.setFieldValue(_booleanField, NoYes::Yes);
                ReqPlanSched.setFieldValue(_timeFenceField, 0);
                _group.enabled(false);
            }
            break;
    }
}

private ReqYesOverrideCombo getAndSetupTimeFenceCoverageValue()
{
    if (ReqPlanSched.TimeFenceCovFixed)
    {
        CovGroupDetails.enabled(true);
        return ReqYesOverrideCombo::YesOverride;
    }
    CovGroupDetails.enabled(false);
    return ReqYesOverrideCombo::YesCoverageGroup;
}

