public void activate(boolean _active)
{
    super(_active);
    this.doActiveFields();
}

public void closeOk()
{
    super();
    ReqPlanSched.update();
    ReqParameters.update();
    if (ReqMRPSetupWizardParameters.RecId)
    {
        ReqMRPSetupWizardParameters.update();
    }
    else
    {
        ReqMRPSetupWizardParameters.insert();
    }

}

private boolean hideMasterPlanningParametersInSummaryGroup()
{
    return !JobSchedulingSummary                .isVisible() && !GroupCovReview                      .isVisible()
            && !GroupLockingReview                  .isVisible() && !GroupAuthorizationReview            .isVisible()
            && !GroupMasterPlanReview               .isVisible() && !GroupCapacityReview                 .isVisible()
            && !GroupActionReview                   .isVisible() && !GroupTimefenceBackRequisitionReview .isVisible()
            && !PMFSeqTimeFenceSum                  .isVisible() && !GroupFuturesReview                  .isVisible()
            && !DelaysSummary                       .isVisible() && !GroupExplosionReview                .isVisible();
}

private void displaySummaryPage()
{
    if ( !this.showAll &&
                ReqParameters.DefaultNumberOfThreads   == ReqParametersOrig.DefaultNumberOfThreads  &&
                ReqParameters.BundleSize               == ReqParametersOrig.BundleSize              &&
                ReqParameters.FirmingBundleSize        == ReqParametersOrig.FirmingBundleSize       &&
                ReqParameters.UseCache                 == ReqParametersOrig.UseCache)
    {
        ChangesToReqParameters.visible(false);
    }
    else
    {
        ThreadsGroup        .visible(this.showAll || element.displayChangeReqParameters(fieldStr(ReqParameters,DefaultNumberOfThreads)));
        BundlesGroup        .visible(this.showAll || element.displayChangeReqParameters(fieldStr(ReqParameters,BundleSize)));
        FirmingBundlesGroup .visible(this.showAll || element.displayChangeReqParameters(fieldStr(ReqParameters,FirmingBundleSize)));
        CacheGroup          .visible(this.showAll || element.displayChangeReqParameters(fieldStr(ReqParameters,UseCache)));
        ChangesToReqParameters.visible(true);
    }

    JobSchedulingSummary                .visible(ReqPlanSched.CovSchedMethod != ReqPlanSchedOrig.CovSchedMethod);
    GroupCovReview                      .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceCovFixed), fieldStr(ReqPlanSched,TimeFenceCov)));
    GroupLockingReview                  .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceLockingFixed), fieldStr(ReqPlanSched,TimeFenceLocking)));
    GroupAuthorizationReview            .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceAuthorizationFixed), fieldStr(ReqPlanSched,TimeFenceAuthorization)));
    GroupMasterPlanReview               .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceMasterPlanFixed), fieldStr(ReqPlanSched,TimeFenceMasterPlan)));
    GroupCapacityReview                 .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceCapacityFixed), fieldStr(ReqPlanSched,TimeFenceCapacity)));
    GroupActionReview                   .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceActionFixed),fieldStr(ReqPlanSched,TimeFenceAction)));
    GroupTimefenceBackRequisitionReview .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceBackRequisitionFixed), fieldStr(ReqPlanSched,TimeFenceBackRequisition)));
    PMFSeqTimeFenceSum                  .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,PMFSeqTimeFenceSequencingFixed), fieldStr(ReqPlanSched,PMFSeqTimeFenceSequencing)));
    GroupFuturesReview                  .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceFuturesFixed), fieldStr(ReqPlanSched,TimeFenceFutures)));
    DelaysSummary                       .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceFuturesFixed), fieldStr(ReqPlanSched,TimeFenceFutures))
                                                            || ReqPlanSchedOrig.PreventScheduleBeforeToday != ReqPlanSched.PreventScheduleBeforeToday);
    GroupExplosionReview                .visible(element.displayChangeReqPlanSched(fieldStr(ReqPlanSched,TimeFenceExplosionFixed), fieldStr(ReqPlanSched,TimeFenceExplosion)));

    if (this.hideMasterPlanningParametersInSummaryGroup())
    {
        ChangesToReqPlanSched.visible(false);
    }
    else
    {
        ChangesToReqPlanSched.visible(true);
    }

    NoChangesText.visible(!ChangesToReqPlanSched.isVisible() && !ChangesToReqParameters.isVisible());
}

