public boolean tabChange(int _FromTab)
{
    boolean ret;

    ret = super(_FromTab);

    switch(_FromTab)
    {
        case PlanOptimizationPageNumber:
            {
                if (!ReqMRPSetupWizardParameters.NumberOfItems)
                {
                    ret = checkFailed(strFmt("@SYS78463", "@SYS28571"));
                }
                if (!ReqMRPSetupWizardParameters.MaxBomLevel)
                {
                    ret = checkFailed(strFmt("@SYS78463", "@SYS24262"));
                }
                if (!ReqMRPSetupWizardParameters.NumberOfPlannedOrders)
                {
                    ret = checkFailed(strFmt("@SYS78463", "@SCMPlanning:ReqMRPNumberOfPlannedOrders"));
                }
                if (ret)
                {
                    this.setNumOfThreads();
                    ReqParameters.BundleSize = 1;
                    ReqParameters.UseCache = ReqUseCache::Maximum;

                }
            }
            break;
        case PlanScopePageNumber:
            if (ReqPlanSched.TimeFenceCovFixed && !ReqPlanSched.TimeFenceCov)
            {
                ret = checkFailed("@SCMPlanning:ReqMRPCoverageError");
            }
            break;
        case PlanDemandUpdatesPageNumber:
        {
            ret =  this.setFirmingBundle();
            break;
        }
    }

    return ret;
}

private void setNumOfThreads()
{
    Integer numOfThreads = ReqParameters.DefaultNumberOfThreads;
    if (ReqMRPSetupWizardParameters.IsManufacturer)
    {
        if (ReqMRPSetupWizardParameters.NumberOfPlannedOrders)
        {
            numOfThreads = real2int(ReqMRPSetupWizardParameters.NumberOfPlannedOrders / 1000.0);
        }
    }
    else
    {
        if (ReqMRPSetupWizardParameters.NumberOfItems)
        {
            numOfThreads = real2int(ReqMRPSetupWizardParameters.NumberOfItems / 1000.0);
        }
    }

    int maxThreads = real2int(0.75 * ReqParameters::getMaxNumberOfAvailableThreads());

    if (numOfThreads > maxThreads)
    {
        numOfThreads = maxThreads;
    }
    else if (numOfThreads < 1)
    {
        numOfThreads = 0;
    }

    ReqParameters.DefaultNumberOfThreads = numOfThreads;
}

private boolean setFirmingBundle()
{           
    int firmBundle = 0;
    if (ReqPlanSched.TimeFenceAuthorization && ReqPlanSched.TimeFenceCov && ReqParameters.DefaultNumberOfThreads &&
            ReqMRPSetupWizardParameters.NumberOfPlannedOrders > ReqParameters.DefaultNumberOfThreads)
    {
        firmBundle = real2int((ReqMRPSetupWizardParameters.NumberOfPlannedOrders * (ReqPlanSched.TimeFenceAuthorization/ ReqPlanSched.TimeFenceCov)
                                            / ReqParameters.DefaultNumberOfThreads)/10);
    }
    ReqParameters.FirmingBundleSize = max(firmBundle, ReqMRPSetupWizardParameters.IsManufacturer ? 10 : 50);

    return true; 
}

public void tabChanged(int _FromTab, int _ToTab)
{
    super(_FromTab, _ToTab);
    if ( _FromTab == PlanDemandUpdatesPageNumber  && _ToTab == _FromTab + 1)
    {
        element.displaySummaryPage();
    }
}

