[Form]
public class ReqMultiLevelPegging extends FormRun implements ReqTransExplosionSetupInterface
{
    ReqMultiLevelPeggingForm   reqMultiLevelPeggingForm;

    QueryBuildRange         rangeRecId;
    QueryBuildRange         rangeReqPlanVersion;
    QueryBuildRange         rangeRefType;
    QueryBuildRange         rangeRefId;
    QueryBuildRange         rangeInventTransOrigin;
    QueryBuildRange         rangeItemId;

}

void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    reqMultiLevelPeggingForm = ReqMultiLevelPeggingForm::newFormRun(element, ReqExplodeType::Up);

    super();

    reqMultiLevelPeggingForm.parmCtrlTree(tree);
    reqMultiLevelPeggingForm.parmCtrlFormatExplain(formatExplain);
    reqMultiLevelPeggingForm.parmCtrlGroupSettlement(groupSettlement);

    reqMultiLevelPeggingForm.formInitPost();

    this.updateExplosionViewButton();
}

public void openMenuItemForReqTrans(MenuItemType _menuItemType, menuItemName _menuItemName, ReqTrans _callerBuffer)
{
    Args            args;
    MenuFunction    menuFunction;

    appl.setDefaultCompany(_callerBuffer.DataAreaId);

    args = new Args();
    args.record(_callerBuffer);
    args.caller(this);
    menuFunction = new MenuFunction(_menuItemName, _menuItemType);
    menuFunction.run(args);
}

void updateExplosionViewButton()
{
    //Arrow direction needs to be inverted, because down means up in multilevel pegging form.
    reqMultiLevelPeggingForm.updateExplosionViewButton(ExplosionTreeView, TreeExplodeDown, TreeExplodeUp, true);
}

public int active()
{
    int ret;

    ret = super();

    reqMultiLevelPeggingForm.build();
    element.initTab();

    reqTransCaption.text(reqTransCaller.tooltipRecord());

    tmpReqTrans_ds.active();

    return ret;
}

public void executeQuery()
{
    ReqTrans    reqTransFind;
    ItemId      itemId = ReqCalc::argsItemId(element.args());

    if (itemId)
    {
        ReqTransUpdate::runItemId(itemId);
    }

    reqTransFind = ReqTrans::findCommon(
        ReqPlan::findReqPlanId(element.reqMultiLevelPeggingFormCaller().parmReqPlanId()).findActiveVersion().RecId,
        element.args().record());

    rangeReqPlanVersion.value(queryValue(reqTransFind.PlanVersion));
    rangeRefType.value(queryValue(reqTransFind.RefType));
    rangeRefId.value(queryValue(reqTransFind.RefId));
    rangeInventTransOrigin.value(queryValue(reqTransFind.InventTransOrigin));
    rangeItemId.value(queryValue(reqTransFind.ItemId));
    rangeRecId.value(SysQuery::valueUnlimited());

    super();

    if (! reqTransCaller.RecId)
    {
        reqMultiLevelPeggingForm.build();
        element.initTab();
    }
}

public void init()
{
    Query                   query = new Query();
    QueryBuildDataSource    qbs;

    super();

    reqMultiLevelPeggingForm.reqTransCallerDSInitPost(reqTransCaller);

    qbs = query.addDataSource(tableNum(ReqTrans));
    qbs.name(reqTransCaller_ds.name());

    rangeReqPlanVersion     = qbs.addRange(fieldNum(ReqTrans,PlanVersion));
    rangeRefType            = qbs.addRange(fieldNum(ReqTrans,RefType));
    rangeRefId              = qbs.addRange(fieldNum(ReqTrans,RefId));
    rangeInventTransOrigin  = qbs.addRange(fieldNum(ReqTrans,InventTransOrigin));
    rangeItemId             = qbs.addRange(fieldNum(ReqTrans,ItemId));

    rangeRecId              = qbs.addRange(fieldNum(ReqTrans,RecId));

    this.query(query);
}

public display PdsCWQtyCoverage displayPdsCWCovQty(ReqTrans _reqTrans)
{
    ReqTrans reqTransNonTmp = this.reqTransNonTmp(_reqTrans);
    changeCompany(reqTransNonTmp.company())
    {
        return reqTransNonTmp.pdsCWCovQty();
    }
}

public display PdsCWReqQty displayPdsCWReqQty(ReqTrans _reqTrans)
{
    ReqTrans reqTransNonTmp = this.reqTransNonTmp(_reqTrans);
    changeCompany(reqTransNonTmp.company())
    {
        return reqTransNonTmp.pdsCWReqQty();
    }
}

display UnitOfMeasureSymbol displayUnitId(ReqTrans _reqTrans)
{
    ReqTrans reqTransNonTmp = this.reqTransNonTmp(_reqTrans);
    changeCompany(
                  reqTransNonTmp.company()
                  )
    {
        return reqTransNonTmp.unitOfMeasure();
    }
}

public int active()
{
    int ret;

    ret = super();

    reqMultiLevelPeggingForm.reqTransDSActivePost();

    return ret;
}

public void init()
{
    Query                query;
    QueryBuildDataSource qbs;

    super();

    tmpReqTrans.setTmp();
    tmpReqTrans.checkRecord(false);

    reqMultiLevelPeggingForm.reqTransDSInitPost(tmpReqTrans);

    query = new Query();
    qbs   = query.addDataSource(tablenum(ReqTrans));
    qbs.name(tmpReqTrans_ds.name());

    query.allowCrossCompany(true);

    qbs.addSortField(fieldnum(ReqTrans,Level));
    qbs.addSortField(fieldnum(ReqTrans,RefId));

    this.query(query);
}

public void jumpRef()
{
    ReqTrans reqTransNonTmp = tmpReqTrans_ds.reqTransNonTmp();

    appl.setDefaultCompany(reqTransNonTmp.DataAreaId);
    reqTransNonTmp.jumpRefRefId(element);
}

public display PdsCWSettled pdsCWCovQty(ReqTransCov _reqTransCov)
{
    PdsCWSettled    cwQty;

    changeCompany(_reqTransCov.company())
    {
        cwQty = _reqTransCov.pdsCWSettled();
    }
    return tmpReqTrans.Qty >= 0 ? cwQty : -cwQty;
}

public void init()
{
    Query query;

    super();

    reqMultiLevelPeggingForm.reqTransCovDSInitPost(reqTransCov,reqTransSettled);

    query = reqTransCov_ds.query();
    query.allowCrossCompany(true);
}

public display PdsCWReqQty displayPdsCWReqQty(ReqTrans _reqTrans)
{
    changeCompany(_reqTrans.company())
    {
        return _reqTrans.pdsCWReqQty();
    }
}

public void clicked()
{
    super();
    // Down means "down the tree/graph", which corresponds to upstream supply
    reqMultiLevelPeggingForm.parmReqExplodeType(ReqExplodeType::Down);
    reqMultiLevelPeggingForm.build();

    element.updateExplosionViewButton();
}

public void clicked()
{
    super();
    // Up means "up the tree/graph", which corresponds to downstream demand
    reqMultiLevelPeggingForm.parmReqExplodeType(ReqExplodeType::Up);
    reqMultiLevelPeggingForm.build();

    element.updateExplosionViewButton();
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    super(_oldItem, _newItem, _how);

    if (_oldItem && _newItem.idx())
    {
        reqMultiLevelPeggingForm.newIdSave(_newItem.idx());
        reqMultiLevelPeggingForm.treeSelectionChange();
    }
}

boolean expanding(int idx, FormTreeExpand action, anytype data)
{
    boolean ret;

    if (action == FormTreeExpand::Expand)
    {
        reqMultiLevelPeggingForm.treeBuildNode(idx);
    }

    ret = super(idx, action, data);

    return ret;
}

void clicked()
{
    boolean showCapacity;
    boolean showDerived;

    showCapacity = ReqTrans::refTypeCanReserveWorkCenter(tmpReqTrans.RefType);
    showDerived  = ReqTrans::derivedRefType(tmpReqTrans.RefType) != ReqRefType::None;

    buttonReqTransDerived.visible(showDerived);
    separatorReqTransDerived.visible(showDerived);

    buttonReqRoute.visible(     ReqTrans::refTypeCanHaveRoute(tmpReqTrans.RefType));
    buttonReqRouteJobs.visible( ReqTrans::refTypeCanHaveRoute(tmpReqTrans.RefType));
    buttonProdBOM.visible(      tmpReqTrans.RefType == ReqRefType::Production      ? true : false);
    buttonProdRoute.visible(    tmpReqTrans.RefType == ReqRefType::Production      ? true : false);
    buttonProdRouteJob.visible( tmpReqTrans.RefType == ReqRefType::Production      ? true : false);

    separatorBOMRoute.visible(showCapacity);
    separatorWrkCtrCapRes.visible(showCapacity);
    buttonWrkCtrCapRes.visible(showCapacity);

    super();
}
