[Form]
public class ReqOutboundIntercompanyDemand extends FormRun
{
    ReqIntercompanyQueryFactory queryFactory;

    InventDimCtrl_Frm   inventDimFormSetup;
}

public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    super();

    queryFactory = ReqIntercompanyQueryFactory::construct();

    this.initializePlanFilter();
    this.initializeQuery();

    inventDimFormSetup = InventDimCtrl_Frm::construct();
    inventDimFormSetup.parmCallingElement(InventDimAxFormAdapter::newFromForm(this));
    inventDimFormSetup.init();

    this.updateDesign(InventDimFormDesignUpdate::Init);
}

private void initializePlanFilter()
{
    var reqPlanId = ReqTransPlanIdFilter::getPlanIdFromArgsSysFilter(element.args());

    if (reqPlanId)
    {
        planFilter.text(reqPlanId);
    }
    else
    {
        planFilter.text(ReqPlanSched::defaultDynamicId());
    }
}

public void initializeQuery(boolean _reQuery = false)
{
    reqIntercompanyOutboundDemand_ds.query(queryFactory.getOutboundDemandQuery(planFilter.text()).query());

    if (_reQuery)
    {
        reqIntercompanyOutboundDemand_ds.executeQuery();
        reqIntercompanyOutboundDemand_ds.refresh();
    }
}

public Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
///     Opens the specified menu item in the specified company.
/// </summary>
/// <param name="_company">
///     The company in which to open the menu item.
/// </param>
/// <param name="_menuFunction">
///     The menu function to execute.
/// </param>
/// <param name="_record">
///     The record to pass along to the menu function.
/// </param>
public void openMenuItemInCompany(dataAreaId _company, MenuItemName _menuItemName, MenuItemType _menuItemType, Common _record)
{
    Args args;

    appl.setDefaultCompany(_company);

    args = new Args();
    args.caller(this);
    args.record(_record);

    var menuFunction = new MenuFunction(_menuItemName, _menuItemType);
    menuFunction.run(args);
}

private void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            inventDimParm.initCovDimAllGrp();
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm.data());
            inventDimFormSetup.formSetControls(true);
            break;

        default : throw error(strFmt("@SYS54195", funcName()));
    }
}

public void updateVendorDemandDetails()
{
    Query plannedDemandQuery = queryFactory.getPlannedIntercompanyDemandQuery(
        reqIntercompanyOutboundDemand.VendorCompanyId, reqIntercompanyOutboundDemand.PlannedOrder).query();
    QueryBuildDataSource qbdsInventDimUpstream;

    qbdsInventDimUpstream = plannedDemandQuery
        .dataSourceTable(tableNum(ReqUpstreamIntercompanyDemand))
        .addDataSource(tableNum(InventDim), inventdimupstream_ds.name());

    qbdsInventDimUpstream.clearLinks();

    qbdsInventDimUpstream.addLink(
        fieldNum(ReqUpstreamIntercompanyDemand, CovInventDimId),
        fieldNum(InventDim, InventDimId));

    reqUpstreamIntercompanyDemand_ds.query(plannedDemandQuery);
    reqUpstreamIntercompanyDemand_ds.executeQuery();
}

public void executeQuery()
{
    super();

    if (!reqIntercompanyOutboundDemand)
    {
        element.updateVendorDemandDetails();
    }
}

public int active()
{
    int ret;

    ret = super();

    element.updateVendorDemandDetails();

    return ret;
}

public void jumpRef()
{
    InventTable::jumpRefItemId(reqIntercompanyOutboundDemand.ItemId);
}

public void jumpRef()
{
    CompanyInfo::jumpRefCompanyInfo(CompanyInfo::findDataArea(reqIntercompanyOutboundDemand.PlannedOrderDataAreaId));
}

public void jumpRef()
{
    ReqPO::jumpRefRefId(element, reqIntercompanyOutboundDemand.RefId, reqIntercompanyOutboundDemand.PlanVersion);
}

public void jumpRef()
{
    CompanyInfo::jumpRefCompanyInfo(CompanyInfo::findDataArea(reqIntercompanyOutboundDemand.VendorCompanyId));
}

/// <summary>
/// Converts the current quantity to a catch weight quantity.
/// </summary>
/// <returns>
/// The catch weight quantity equivalent of the current record's quantity.
/// </returns>
public display PdsCWReqQty pdsCWReqQty()
{
    PdsCWInventQty pdsCWInventQty;

    if (ReqUpstreamIntercompanyDemand.UpstreamDataAreaId)
    {
        changeCompany(ReqUpstreamIntercompanyDemand.UpstreamDataAreaId)
        {
            pdsCWInventQty = PdsCatchWeight::cwQty(ReqUpstreamIntercompanyDemand.ItemId, ReqUpstreamIntercompanyDemand.Qty);
        }
    }

    return pdsCWInventQty;
}

/// <summary>
/// The catch weight units, if applicable, of the current record.
/// </summary>
/// <returns>
/// The catch weight unit. If not applicable, an empty string is returned.
/// </returns>
public display PdsCWUnitId pdsCWUnitId()
{
    PdsCWUnitId pdsCWUnitId;

    if (ReqUpstreamIntercompanyDemand.UpstreamDataAreaId)
    {
        changeCompany(ReqUpstreamIntercompanyDemand.UpstreamDataAreaId)
        {
            pdsCWUnitId = PdsCatchWeightItem::find(ReqUpstreamIntercompanyDemand.ItemId).PdsCWUnitId;
        }
    }

    return pdsCWUnitId;
}

public void jumpRef()
{
    CompanyInfo::jumpRefCompanyInfo(CompanyInfo::findDataArea(reqUpstreamIntercompanyDemand.UpstreamDataAreaId));
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tablenum(InventDim));

    qbds.clearLinks();
    qbds.clearDynalinks();

    qbds.addDynalink(
        fieldnum(InventDim, InventDimId),
        reqIntercompanyOutboundDemand,
        fieldnum(ReqIntercompanyOutboundDemand, CovInventDimId));
}

void clicked()
{
    Args args;

    args = new Args();
    args.record(ReqPO::findRecId(reqIntercompanyOutboundDemand.PlannedOrder));
    args.caller(element);

    this.menufunction().run(args);
}

void clicked()
{
    Args args;

    args = new Args();
    args.record(InventTable::find(reqIntercompanyOutboundDemand.ItemId));
    args.parm(planFilter.text());
    args.caller(element);

    this.menufunction().run(args);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.initializeQuery(true);
    }

    return ret;
}

void clicked()
{
    ReqTrans reqTrans;

    if (reqUpstreamIntercompanyDemand.RecId)
    {
        changeCompany (ReqUpstreamIntercompanyDemand.UpstreamDataAreaId)
        {
            reqTrans = ReqTrans::findRecId(reqUpstreamIntercompanyDemand.RecId);
        }

        element.openMenuItemInCompany(
            reqUpstreamIntercompanyDemand.UpstreamDataAreaId,
            this.menuItemName(),
            this.menuItemType(),
            reqTrans);
    }
}

