Method.Source:
    [Form]
    public class ReqPlanSched extends FormRun
    {
        SysFormEnumComboBox     periodType;
}

/// <summary>
///     Replaces default lookup datasource in case it does not match main datasource on the form.
/// </summary>
/// <remarks>
///     In some cases when this form is opened via View Details function <c>ReqPlanSched</c> is passed
///     as lookup datasource. However this form uses ReqPlan as it's main datasource - this should be adjusted in order for the
///     View Details to disambiguate the lookup argumets.
/// </remarks>
private void adjustLookupDataSource()
{
    Args args = element.args();

    if ( args.lookupTable() == tableNum(ReqPlanSched) && args.lookupField() == fieldNum(ReqPlanSched,ReqPlanIdSched) )
    {
        args.lookupTable(tableNum(ReqPlan));
        args.lookupField(fieldNum(ReqPlan,ReqPlanId));
    }
}

void doActiveFields()
{
    if (! reqPlanSched.CovSchedMethod)
    {
        reqPlanSched.CovPropertyLimited = NoYes::No;
        reqPlanSched_ds.object(fieldNum(ReqPlanSched,CovPropertyLimited)).enabled(false);
    }
    else
    {
        reqPlanSched_ds.object(fieldNum(ReqPlanSched,CovPropertyLimited)).enabled(true);
    }

    reqPlanSched_ds.object(fieldNum(ReqPlanSched,TimeFenceFiniteCap)).enabled(reqPlanSched.CovCapLimited);
    reqPlanSched_ds.object(fieldNum(ReqPlanSched,BottleneckScheduling)).enabled(reqPlanSched.CovCapLimited);
    reqPlanSched_ds.object(fieldNum(ReqPlanSched,TimeFenceBottleneckCap)).enabled(reqPlanSched.CovCapLimited && reqPlanSched.BottleneckScheduling);

    reqPlanSched_ds.object(fieldNum(ReqPlanSched,SmmQuotationProbabilityId)).enabled(reqPlanSched.UseQuotations);

    reqPlanSched_ds.object(fieldNum(ReqPlanSched, PMFSeqBucketPeriod)).enabled(ReqPlanSched.PMFSeqBucketType == PMFSeqCalendarPeriod::Calendar);

    // Continuity master planning plugin
    reqPlanSched_ds.object(fieldnum(ReqPlanSched,MCRPastDueContinuityDays)).enabled(reqPlanSched.MCRIncludePastDueContinuity);

    reqPlanSched_ds.object(fieldnum(ReqPlanSched,OnHandConsumptionStrategy)).enabled(ReqPlanSched.OnHandConsumptionStrategyFixed);
    reqPlanSched_ds.object(fieldnum(ReqPlanSched,MustUseLatestPossibleSupplyFirst)).enabled(ReqPlanSched.OnHandConsumptionStrategyFixed);

    MasterPlanMapping.enabled(ReqPlanSched.IncludePlannedIntercompanyDemand);
}

public void init()
{
    ReqParameters::warnIfPlanningProcessesAreDisabled();

    super();

    element.initPDSPeriodType();

    this.adjustLookupDataSource();

    HelpText.visible(ReqDeprecatedFunctionalitySharedParameters::find().IsIntercompanyGroupDownstreamDemandDeprecated);
}

public void initPDSPeriodType()
{
    Set pdsAllowedValue;

    if (!periodType)
    {
        pdsAllowedValue = new Set(Types::Enum);
    }

    if (ReqPlanSched.PMFSeqBucketType == PMFSeqCalendarPeriod::Period)
    {
        pdsAllowedValue.add(DayWeekMonth::Day);
        pdsAllowedValue.add(DayWeekMonth::Week);
        pdsAllowedValue.add(DayWeekMonth::Month);
    }
    else
    {
        pdsAllowedValue.add(DayWeekMonth::Week);
        pdsAllowedValue.add(DayWeekMonth::Month);
        pdsAllowedValue.add(DayWeekMonth::Quarter);
    }

    periodType = SysFormEnumComboBox::newParameters(element, PDSSchedule_PMFSeqBucketPeriod.id(), enumNum(DayWeekMonth), pdsAllowedValue);
}

