public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tableNum(ReqIntercompanyMasterPlanMapping));

    //the table has two relations to ReqPlanSched, make sure the correct relation is used
    qbds.clearDynalinks();

    qbds.addDynalink(
        fieldNum(ReqIntercompanyMasterPlanMapping, VendorPlanId),
        reqPlanSched,
        fieldNum(ReqPlanSched, ReqPlanIdSched));

    qbds.addDynalink(
        fieldNum(ReqIntercompanyMasterPlanMapping, VendorDataAreaId),
        reqPlanSched,
        fieldNum(ReqPlanSched, DataAreaId));
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.doActiveFields();
    }

    return ret;
}

