void displayFields()
{
    reqPo_IntvQr.visible(ctrlStatPeriod.selection()         == ReqPoStatPeriod::Quarter);
    intvQrName.visible(ctrlStatPeriod.selection()           == ReqPoStatPeriod::Quarter);

    reqPo_IntvMth.visible(ctrlStatPeriod.selection()        == ReqPoStatPeriod::Month);
    intvMthName.visible(ctrlStatPeriod.selection()          == ReqPoStatPeriod::Month);

    reqPo_IntvWk.visible(ctrlStatPeriod.selection()         == ReqPoStatPeriod::Week);
    intvWkName.visible(ctrlStatPeriod.selection()           == ReqPoStatPeriod::Week);

    reqPo_ReqDate.visible(ctrlStatPeriod.selection()        == ReqPoStatPeriod::ReqDate);

    reqPo_ItemId.visible(ctrlStatKeyItem.selection()        == ReqPoStatKeyItem::ItemId);
    itemName.visible(ctrlStatKeyItem.selection()            == ReqPoStatKeyItem::ItemId);

    reqPo_VendId.visible(ctrlStatKeyVendor.selection()      == ReqPoStatKeyVendor::VendId);
    vendName.visible(ctrlStatKeyVendor.selection()          == ReqPoStatKeyVendor::VendId);

    reqPo_VendGroupId.visible(ctrlStatKeyVendor.selection() == ReqPoStatKeyVendor::VendGroupId);
    vendGroupName.visible(ctrlStatKeyVendor.selection()     == ReqPoStatKeyVendor::VendGroupId);

    reqPo_ItemGroupId.visible(ctrlStatKeyItem.selection()   == ReqPoStatKeyItem::ItemGroupId);
    itemGroupName.visible(ctrlStatKeyItem.selection()       == ReqPoStatKeyItem::ItemGroupId);

    reqPo_Qty.visible(ctrlReqQtyAmount.selectionAsReqQtyAmount()          == ReqQtyAmount::ReqQty);
    reqPo_costAmount.visible(ctrlReqQtyAmount.selectionAsReqQtyAmount()   == ReqQtyAmount::CostAmount);

    reqPo_IsForecastPurch.visible(ctrlPerRefType.value());
    reqPo_RefType.visible(ctrlPerRefType.value());

    ctrlvendId.enabled(ctrlStatKeyVendor.selection()        == ReqPoStatKeyVendor::VendId);
    ctrlvendGroupId.enabled(ctrlStatKeyVendor.selection()   == ReqPoStatKeyVendor::VendGroupId);

    ctrlItemId.enabled(ctrlStatKeyItem.selection()          == ReqPoStatKeyItem::ItemId);
    ctrlItemGroupId.enabled(ctrlStatKeyItem.selection()     == ReqPoStatKeyItem::ItemGroupId);
}

void doResearch(boolean _lockElement = true)
{
    element.initCaption();

    reqPo_DS.executeQuery();

    if (tabPageGraphics.isActivePage())
    {
        element.showGraphics();
    }
}

public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    super();

    element.setReqPlanId();

    xSysLastValue::getLast(this);

    dimFieldListEnumerator = InventDim::dimFieldListEnumerator();
    element.displayFields();

    element.updateDesign(InventDimFormDesignUpdate::Init);
    element.initCaption();
}

