RealBase realBase(ReqPO _reqPO)
{
    if (ctrlReqQtyAmount.selectionAsReqQtyAmount() == ReqQtyAmount::ReqQty)
        return _reqPO.Qty;

    return _reqPO.CostAmount;
}

