{
    reqPlanId = _reqPlanId;

    return reqPlanId;
}

void setReqPlanId()
{
    reqPlanId = ReqCalc::argsReqPlanId(element.args());

    if (! reqPlanId)
    {
        reqPlanId = ReqPlanSched::defaultStaticId();
    }

    planFilter.text(reqPlanId);
}

void showGraphics()
{
    ReqPO       reqPoCache;
    Integer     mark;

    str         strPeriod;
    str         strSeries;
    str         strBase;
    int         periodKey;

    ReqPoItemStatisticChartTmp  chartDataTmp;

    reqPoCache = reqPo_DS.getFirst(1, false);
    if (! reqPoCache.PlanVersion && ! element.strKey(reqPoCache) && ! element.strPeriod(reqPoCache) && element.realBase(reqPoCache) == 0)
    {
        mark = 0;
    }
    else
    {
        mark = 1;
    }

    reqPoCache = reqPo_DS.getFirst(mark, true);
    while (reqPoCache)
    {
        switch (ctrlStatPeriod.selection())
        {
            case ReqPoStatPeriod::Quarter :
                periodKey = reqPoCache.IntvQr;
                break;

            case ReqPoStatPeriod::Month :
                periodKey = reqPoCache.IntvMth;
                break;

            case ReqPoStatPeriod::Week :
                periodKey = reqPoCache.IntvWk;
                break;

            case ReqPoStatPeriod::ReqDate :
                periodKey = date2num(reqPoCache.ReqDate);
                break;
        }

        if (element.realBase(reqPoCache) > 0)
        {
            strPeriod   = element.strPeriod(reqPoCache);
            strSeries   = element.strKey(reqPoCache);

            chartDataTmp.clear();
            chartDataTmp.PeriodKey = periodKey;
            chartDataTmp.PeriodName = strPeriod ? strPeriod : enum2str(ReqPoStatPeriod::Total);
            chartDataTmp.SeriesName = strSeries ? strSeries : '\'\'';
            chartDataTmp.Qty = element.realBase(reqPoCache);
            chartDataTmp.insert();
        }

        reqPoCache = reqPo_DS.getNext();
    }

    // Clear existing records
    delete_from itemReqChartTmp;

    // Values needs to be filled in correct order so periods are sequenced
    while select chartDataTmp order by PeriodKey, SeriesName
    {
        itemReqChartTmp.clear();
        itemReqChartTmp.PeriodKey = chartDataTmp.PeriodKey;
        itemReqChartTmp.PeriodName = chartDataTmp.PeriodName;
        itemReqChartTmp.SeriesName = chartDataTmp.SeriesName;
        itemReqChartTmp.Qty = chartDataTmp.Qty;
        itemReqChartTmp.insert();
    }

    itemReqChartTmp_ds.executeQuery();
}

