TempStr strKey(ReqPO _reqPO)
{
    TempStr         tempStr;
    InventDim       tmpInventDim;
    fieldId         inventDimFieldId;

    switch (ctrlStatKeyVendor.selection())
    {
        case ReqPoStatKeyVendor::VendId :
            tempStr = _reqPO.VendId;
            break;

        case ReqPoStatKeyVendor::VendGroupId :
            tempStr = _reqPO.VendGroupId;
            break;
    }

    switch (ctrlStatKeyItem.selection())
    {
        case ReqPoStatKeyItem::ItemId :
                    if (tempStr)
            {
                tempStr += '/';
            }
            tempStr += _reqPO.ItemId;
            break;

        case ReqPoStatKeyItem::ItemGroupId :
                    if (tempStr)
            {
                tempStr += '/';
            }
            tempStr += _reqPO.ItemGroupId;
            break;
    }

    tmpInventDim = _reqPO.joinChild();

    dimFieldListEnumerator.reset();
    while (dimFieldListEnumerator.moveNext())
    {
        inventDimFieldId = dimFieldListEnumerator.current();
        if (tmpInventDim.(inventDimFieldId))
        {
            if (tempStr)
            {
                tempStr += '/';
            }
            tempStr += tmpInventDim.dimValue2str(inventDimFieldId);
        }
    }

    if (ctrlPerRefType.value())
    {
        if (tempStr)
        {
            tempStr += '/';
        }
        tempStr += enum2str(_reqPO.RefType);

        if (_reqPO.IsForecastPurch)
        {
            tempStr += '/' + "@SYS17709";
        }
    }

    return tempStr;
}

TempStr strPeriod(ReqPO _reqPO)
{
    TempStr tempStr;

    switch (ctrlStatPeriod.selection())
    {
        case ReqPoStatPeriod::Quarter :
            tempStr = _reqPO.IntvQr ? _reqPO.intvQrName() : '';
            break;

        case ReqPoStatPeriod::Month :
            tempStr = _reqPO.IntvMth ? _reqPO.intvMthName() : '';
            break;

        case ReqPoStatPeriod::Week :
            tempStr = _reqPO.IntvWk ? _reqPO.intvWkName() : '' ;
            break;

        case ReqPoStatPeriod::ReqDate :
            tempStr = _reqPO.ReqDate ? date2str(_reqPO.ReqDate,321,-1,-1,-1,-1,-1, DateFlags::None) : '';
            break;
    }

    return tempStr;
}

public boolean unpack(container packedClass)
{
    Integer  version     = conpeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion :
            [version,#CurrentList] = packedClass;
            break;
        default :
            return false;
    }

    this.list2Value();

    return true;
}

void updateControls()
{
    if (! inUpdateDesign)
        element.doResearch(false);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm   inventDimParm;

    inUpdateDesign = true;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            inventDimParm.initCovDimAllGrp();
            element.inventDimSetupObject().parmDimParmVisibleGrid(inventDimParm.data());

        case InventDimFormDesignUpdate::Active        :
            element.inventDimSetupObject().formSetControls(true,true);
            break;

        default : throw error(strfmt("@SYS54195",funcname()));
    }

    inUpdateDesign = false;
}

public void value2List()
{
    fromDate                    = ctrlFromDate.dateValue();
    toDate                      = ctrlToDate.dateValue();
    statPeriod                  = ctrlStatPeriod.selection();
    statKeyVendor               = ctrlStatKeyVendor.selection();
    statKeyItem                 = ctrlStatKeyItem.selection();
    qtyAmount                   = ctrlReqQtyAmount.selectionAsReqQtyAmount();
    perRefType                  = ctrlPerRefType.value();

    itemId                      = ctrlItemId.text();
    itemGroupId                 = ctrlItemGroupId.text();

    vendId                      = ctrlVendId.text();
    vendGroupId                 = ctrlVendGroupId.text();
}

public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbs;
    QueryBuildDataSource    qbsDim;

    str                     dateRange;
    query = new Query();

    qbs = query.addDataSource(tablenum(ReqPO));
    qbs.name(reqPo_ds.name());

    qbsDim = qbs.addDataSource(tablenum(InventDim));
    qbsDim.name(inventDim_ds.name());

    qbsDim.joinMode(JoinMode::InnerJoin);
    qbsDim.relations(true);
    QueryBuildRange planRange = qbs.addRange(fieldnum(ReqPO,PlanVersion));
    planRange.value(queryValue(
                   ReqPlan::findReqPlanId(reqPlanId).findActiveVersion().RecId
                   ));
    planRange.status(RangeStatus::Hidden);

    if (ctrlFromDate.dateValue() != dateNull())
        dateRange = queryValue(ctrlFromDate.dateValue());

    dateRange += '..';

    if (ctrlToDate.dateValue() != dateNull())
        dateRange += queryValue(ctrlToDate.dateValue());

    if (dateRange != '..')
        qbs.addRange(fieldnum(ReqPO,ReqDate)).value(dateRange);

    switch (ctrlStatKeyVendor.selection())
    {
        case ReqPoStatKeyVendor::VendId :
            qbs.addSortField(fieldnum(ReqPO,VendId));
            if (ctrlVendId.text())
                qbs.addRange(fieldnum(ReqPO,VendId)).value(queryValue(ctrlVendId.text()));
            break;

        case ReqPoStatKeyVendor::VendGroupId :
            qbs.addSortField(fieldnum(ReqPO,VendGroupId));
            if (ctrlVendGroupId.text())
                qbs.addRange(fieldnum(ReqPO,VendGroupId)).value(queryValue(ctrlVendGroupId.text()));

            break;
    }

    switch (ctrlStatKeyItem.selection())
    {
        case ReqPoStatKeyItem::ItemId :
            qbs.addSortField(fieldnum(ReqPO,ItemId));
            if (ctrlItemId.text())
                qbs.addRange(fieldnum(ReqPO,ItemId)).value(queryValue(ctrlItemId.text()));

            break;

        case ReqPoStatKeyItem::ItemGroupId :
            qbs.addSortField(fieldnum(ReqPO,ItemGroupId));
            if (ctrlItemGroupId.text())
                qbs.addRange(fieldnum(ReqPO,ItemGroupId)).value(queryValue(ctrlItemGroupId.text()));

            break;
    }

    switch (ctrlStatPeriod.selection())
    {
        case ReqPoStatPeriod::Quarter :
            qbs.addSortField(fieldnum(ReqPO,IntvQr));
            break;

        case ReqPoStatPeriod::Month :
            qbs.addSortField(fieldnum(ReqPO,IntvMth));
            break;

        case ReqPoStatPeriod::Week :
            qbs.addSortField(fieldnum(ReqPO,IntvWk));
            break;

        case ReqPoStatPeriod::ReqDate :
            qbs.addSortField(fieldnum(ReqPO,ReqDate));
            break;
    }

    qbs.addSortField(fieldnum(ReqPO,PlanVersion));

    if (ctrlPerRefType.value())
    {
        qbs.addSortField(fieldnum(ReqPO,RefType));
        qbs.addSortField(fieldnum(ReqPO,IsForecastPurch));
    }

    qbs.orderMode(OrderMode::GroupBy);

    qbs.fields().addField(fieldnum(ReqPO,Qty),SelectionField::Sum);
    qbs.fields().addField(fieldnum(ReqPO,CostAmount),SelectionField::Sum);

    element.inventDimSetupObject().parmDimParmVisibleGrid().queryAddSortField(qbsDim);

    qbsDim.addSortIndex(indexnum(InventDim,DimIdIdx));
    qbsDim.indexIsHint(true);

    query.literals(2);

    reqPo_ds.query(query);

    super();
}

