[Form]
public class ReqRoute extends FormRun
{
    ReqTrans            reqTransCaller;

    QueryBuildRange     criteriaReqPlan;
    QueryBuildRange     criteriaRefNum;

    QueryBuildRange     criteriaProdId;

}

public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    FormRun     formRun;

    reqTransCaller = ReqTrans::common2ReqTrans(element.args().record());

    super();

    if (reqTransCaller && reqTransCaller.RefType == ReqRefType::Production)
    {
        element.args().name(formstr(ProdRoute));
        element.args().record(reqTransCaller);

        formRun = classfactory.formRunClass(element.args());
        formRun.init();
        formRun.run();
        formRun.detach();

        element.close();
    }
}

public void initActivityDataSource(FormDataSource _formDataSource)
{
    SysDictTable sysDictTable = new SysDictTable(_formDataSource.table());

    if (!isConfigurationkeyEnabled(sysDictTable.configurationKeyId()))
    {
        _formDataSource.linkType(FormLinkType::Passive);
    }
}

/// <summary>
/// Gets the site of the planned production order related to the operation.
/// </summary>
/// <returns>
/// The site of the planned production order.
/// </returns>
public InventSiteId siteId()
{
    return ReqPO::find(reqRoute.PlanVersion, reqRoute.RefId).inventDim().InventSiteId;
}

public void executeQuery()
{
    if (element.args().record() && element.args().dataset() == tableNum(ReqPlannedOrderRouteOperationStaging)) 
    {
        ReqPlannedOrderRouteOperationStaging staging = element.args().record();
        ReqPlanVersion planVersion = ReqPlanVersion::findActiveReqPlanId(staging.RequirementPlanId, staging.RouteOperationDataAreaId);
        ReqRoute plannedOrderRoute = ReqRoute::find(planVersion.RecId, staging.PlannedOrderNumber, staging.OperationNumber, staging.OperationPriority);

        this.queryBuildDataSource().addRange(fieldNum(ReqRoute, RecId)).value(queryValue(plannedOrderRoute.RecId));
    }
    else if (element.args().record() && element.args().dataset() == tableNum(ReqPlannedOrderRouteOperationResourceRequirementStaging))
    {
        ReqPlannedOrderRouteOperationResourceRequirementStaging staging = element.args().record();
        ReqPlanVersion planVersion = ReqPlanVersion::findActiveReqPlanId(staging.RequirementPlanId, staging.RouteOperationDataAreaId);
        ReqRoute plannedOrderRoute = ReqRoute::find(planVersion.RecId, staging.PlannedOrderNumber, staging.RouteOperationNumber, staging.RouteOperationPriority);

        this.queryBuildDataSource().addRange(fieldNum(ReqRoute, RecId)).value(queryValue(plannedOrderRoute.RecId));
    }
    super();

    reqRoute_DS.findCaller();
}

void findCaller()
{
    ReqRoute    reqRouteTmp;
    switch (element.args().dataset())
    {
        case (tableNum(ReqRoute)):

            reqRouteTmp = element.args().record();

            if (reqRouteTmp.RecId)
            {
                reqRoute_ds.positionToRecord(reqRouteTmp);
            }

            break;
    }
}

public void init()
{
    QueryBuildDataSource qbs;

    super();

    qbs = this.query().dataSourceName(reqRouteSchedulingView_ds.name());

    qbs.clearDynalinks();

    qbs.addDynalink(fieldNum(ReqRouteSchedulingView, PlanVersion),  reqRoute, fieldNum(ReqRoute, PlanVersion));
    qbs.addDynalink(fieldNum(ReqRouteSchedulingView, RefId),        reqRoute, fieldNum(ReqRoute, RefId));
    qbs.addDynalink(fieldNum(ReqRouteSchedulingView, OprNum),       reqRoute, fieldNum(ReqRoute, OprNum));
    qbs.addDynalink(fieldNum(ReqRouteSchedulingView, OprPriority),  reqRoute, fieldNum(ReqRoute, OprPriority));
}

