private void addInventoryDimensionRanges(FormDataSource _dataSource, Map _inventDimQueryRanges, boolean _addSiteWarehouseRanges = true)
{
    QueryBuildDataSource qbdsInventDim = _dataSource.query().dataSourceTable(tableNum(InventDim));

    InventDimensionEnumerator productDims = InventProductDimension::getEnumeratorForEnabledDimensions();
    while (productDims.moveNext())
    {
        FieldId productDimFieldId = productDims.currentInventDimFieldId();
        QueryBuildRange qbrProductDimField = qbdsInventDim.addRange(productDimFieldId);
        _inventDimQueryRanges.insert(productDimFieldId, qbrProductDimField);
    }

    if (_addSiteWarehouseRanges)
    {
        _inventDimQueryRanges.insert(fieldNum(InventDim, InventSiteId), qbdsInventDim.addRange(fieldNum(InventDim, InventSiteId)));
        _inventDimQueryRanges.insert(fieldNum(InventDim, InventLocationId), qbdsInventDim.addRange(fieldNum(InventDim, InventLocationId)));
    }
}

public boolean checkActiveMasterPlanVersion()
{
    Dialog dialog;

    if (element.parmReqPlanVersionRecId() == this.selectedPlanVersionRecId())
    {
        return true;
    }

    // Active master plan version changed
    dialog = new Dialog("@SYS324560");

    // The active master plan version have been changed since setting the filtering of the supply demand overview. Press OK to proceed with this setting.
    dialog.addText("@SYS324561");

    return dialog.run();
}

public void close()
{
    // Form part references must be nulled before calling super() otherwise memory will not be freed
    futuresPart     = null;
    actionsPart     = null;
    itemDetailsPart = null;

    reqTransForm.formPreClose();

    super();
}

public ReqRefType currentReqRefType()
{
    return ReqSupplyDemandScheduleModel::findReqRefType(element.currentTableRowType());
}

public int currentTableRowType()
{
    int currentTableRowType;

    if (supplyDemandOverviewTable.rows() && supplyDemandOverviewTable.row() > 0)
    {
        currentTableRowType = conPeek(conPeek(reqSupplyDemandScheduleControl.parmRowStructure(), reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(supplyDemandOverviewTable.row())), 1);
    }

    return currentTableRowType;
}

public InventDim defaultInventDim()
{
    return InventDim::findOrCreate(inventDim);
}

/// <summary>
///     Defines a set of supply demand types.
/// </summary>
public void defineSupplyDemandTypes()
{
    container                       supplyDemandTypes;

    supplyDemandTypes = ReqSupplyDemandTypes::defineSupplyDemandTypes();

    reqSupplyDemandScheduleModel.parmNumberOfSupplyDemandTypes(conLen(supplyDemandTypes));
    reqSupplyDemandScheduleControl.parmRowStructure(supplyDemandTypes);
}

public void delayMessage()
{
    Days    delay;
    Days    maxDelay;

    if (conLen(delays) > 0)
    {
        maxDelay = conPeek(conPeek(delays, 1), supplyDemandOverviewTable.column());

        delay = conPeek(conPeek(delays, 2), supplyDemandOverviewTable.column());
        maxDelay = (delay > maxDelay) ? delay : maxDelay;

        delay = conPeek(conPeek(delays, 3), supplyDemandOverviewTable.column());
        maxDelay = (delay > maxDelay) ? delay : maxDelay;
    }

    if (maxDelay > 0)
    {
        infoMessageArea.visible(true);
        // Orders in the period are delayed up to %1 days
        delayWarningMessage.text(strFmt("@SYS324556", maxDelay));
    }
    else
    {
        // No orders in the period are delayed
        delayWarningMessage.text("@SYS324557");
    }
}

void enableFieldsActive()
{
    // Nothing need to be done
    // Without this method, an error will be thrown in \Classes\ReqTransFormPO\reqPoDSActivePost
    return;
}

public void eventChangeFilter(boolean _updatePeriods)
{
    element.hideCurrentPreview();
    currentPreview = 0;
    currentPreviewPeriod = 0;

    element.resetFocus();

    if (reqPlanId == ReqPlanSched::defaultDynamicId() && itemId != '')
    {
        ReqTransUpdate::runItemId(itemId);
    }

    if (_updatePeriods)
    {
        element.updateTablePeriods();
    }

    element.recalculate();

    element.recalculateDelays();

    element.updateTableRowLabels();

    element.renderData(false);

    element.updateInfoParts();

    element.updateTitleArea();

    element.setInventoryMinMaxLevels();

    element.findReqGroup();

    element.updateInventoryProfileGraph();

    element.updateButtons();

    element.resetFocus();
}

public void eventCollapseNode()
{
    reqSupplyDemandScheduleControl.collapse(supplyDemandOverviewTable.row());

    this.updateTableRowLabels();
    this.renderData(false);
}

public void eventExpandNode()
{
    renderRows = reqSupplyDemandScheduleControl.expand(supplyDemandOverviewTable.row());

    this.updateTableRowLabels();
    this.renderData(true);
}

public void eventOpenForm()
{
    if (!periodTemplate || !reqPlanId || (!(itemId || itemAllocationKey)))
    {
        if (periodTemplate)
        {
            reqSupplyDemandScheduleModel.parmQueryData(false);
            element.updateTablePeriods();
        }

        reqSupplyDemandScheduleModel.parmQueryData(true);

        element.openFilterForm();
    }
    else
    {
        if (reqPlanId == ReqPlanSched::defaultDynamicId() && itemId != '')
        {
            ReqTransUpdate::runItemId(itemId);
        }

        reqSupplyDemandScheduleModel.parmQueryData(false);
        element.updateTablePeriods();

        reqSupplyDemandScheduleModel.parmQueryData(true);
        element.recalculate();

        element.recalculateDelays();

        element.updateTableRowLabels();

        this.renderData(false);

        element.updateInfoParts();

        element.updateTitleArea();

        element.updateInventoryProfileGraph();

        element.resetFocus();
    }
}

public void eventRefresh()
{
    if (reqPlanId == ReqPlanSched::defaultDynamicId() && itemId != '')
    {
        ReqTransUpdate::runItemId(itemId);
    }

    element.recalculate();

    // Get show/hidden state of the rows
    reqSupplyDemandScheduleControl.setTableRowsVisible(reqSupplyDemandScheduleModel.typeVisible());

    element.updateTableRowLabels();

    this.renderData(false);

    element.updateInfoParts();

    element.updateInventoryProfileGraph();
    element.updateSupplyProfileGraph();
}

internal void eventFilterCancelled()
{
    // If no data was loaded prior to cancelling the filter form. Close this form.
    if (!SupplyDemandOverviewTable.rows())
    {
        element.close();
    }
}

public KanbanRule findKanbanRuleForItem()
{
    KanbanFindRules     kanbanFindRules;

    kanbanFindRules = KanbanFindRules::newStandard(itemId,
                                                   DateTimeUtil::newDateTime(element.getFromDate(), 0),
                                                   DateTimeUtil::newDateTime(element.getToDate(), 0),
                                                   InventDim::findDim(element.parmInventDim()).InventDimId);

    kanbanFindRules.parmReplenishmentStrategy(LeanReplenishmentStrategy::Scheduled);

    return kanbanFindRules.findFirstKanbanRule();
}

public void findReqGroup()
{
    if (itemId)
    {
        reqGroup = ReqGroup::find(InventTable::find(itemId).ReqGroupId);
    }
    else
    {
        reqGroup = null;
    }
}

private RecId selectedPlanVersionRecId()
{
    return ReqPlanVersion::findActiveReqPlanId(reqPlanId, curext()).RecId;
}

public TransDate getFromDate()
{
    if (supplyDemandOverviewTable.column() && periodTemplatePeriods && conLen(periodTemplatePeriods) > 0)
    {
        return conPeek(conPeek(periodTemplatePeriods, supplyDemandOverviewTable.column()), #periodFromDate);
    }

    return dateNull();
}

public ReqSupplyDemandScheduleControl getReqSupplyDemandScheduleControl()
{
    return reqSupplyDemandScheduleControl;
}

public TransDate getToDate()
{
    if (supplyDemandOverviewTable.column() && periodTemplatePeriods && conLen(periodTemplatePeriods) > 0)
    {
        return conPeek(conPeek(periodTemplatePeriods, supplyDemandOverviewTable.column()), #periodToDate);
    }

    return dateNull();
}

