public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    ReqItemTable            reqItemTable;
    RefRecId                defaultPeriodTemplate;
    ReqTrans                callerReqTrans;
    ForecastItemAllocation  callerForecastItemAllocation;
    InventDim               callerInventDim;
    InventTable             callerInventTable;
    ReqGroup                reqGroupForPeriodTemplate;
    ReqSetupDim             reqSetupDim;

    #define.collapseIcon("[-] ")
    #define.expandIcon("[+] ")
    #define.indent('      ')

    reqTransForm = ReqTransFormPO::newFormRun(element);

    numberFormatInfo = System.Globalization.CultureInfo::CreateSpecificCulture(xUserInfo::find().PreferredLocale).NumberFormat;
    numberFormatInfo.NumberDecimalDigits = new SysDictType(extendedTypeNum(ReqQty)).effectivePrecision();

    super();

    reqTransForm.parmUseCheckmarks(true);
    reqTransForm.formInitPost();

    reqSupplyDemandScheduleModel = ReqSupplyDemandScheduleModel::construct();

    // Initialize the table control rendering class
    reqSupplyDemandScheduleControl = ReqSupplyDemandScheduleControl::construct();
    reqSupplyDemandScheduleControl.parmCollapse(#collapseIcon);
    reqSupplyDemandScheduleControl.parmExpand(#expandIcon);
    reqSupplyDemandScheduleControl.parmIndent(#indent);
    reqSupplyDemandScheduleControl.parmFormTableControl(supplyDemandOverviewTable);

    element.defineSupplyDemandTypes();
    element.updatePreviewForSelection();

    element.initEditControls();

    if (element.args())
    {
        if (element.args().dataset() == tableNum(ReqTrans))
        {
            callerReqTrans = element.args().record();
            element.parmItemId(callerReqTrans.ItemId);
            callerInventDim = InventDim::find(callerReqTrans.CovInventDimId);
            element.parmInventDim(callerInventDim);
            element.parmReqPlanId(ReqPlanVersion::find(callerReqTrans.PlanVersion).ReqPlanId);
            element.parmReqPlanVersionRecId(callerReqTrans.PlanVersion);

            // Try to find period template based on the item coverage setup
            reqItemTable = ReqItemTable::find(itemId, reqTrans.inventDim().InventDimId);
            reqGroupForPeriodTemplate = ReqGroup::find(reqItemTable.ReqGroupId);
        }
        else
        if (element.args().dataset() == tableNum(ForecastItemAllocation))
        {
            callerForecastItemAllocation = element.args().record();
            element.parmForecastItemAllocationId(callerForecastItemAllocation.AllocateId);
            element.parmReqPlanId(ReqPlanSched::defaultDynamicId());
            element.parmReqPlanVersionRecId(element.selectedPlanVersionRecId());
            bomReportFinishMaxAction.enabled(false);
        }
        else
        if (element.args().dataset() == tableNum(InventTable))
        {
            callerInventTable = element.args().record();
            element.parmItemId(callerInventTable.ItemId);
            element.parmReqPlanId(ReqPlanSched::defaultDynamicId());
            element.parmReqPlanVersionRecId(element.selectedPlanVersionRecId());
        }
        else
        {
            element.parmReqPlanId(ReqTransPlanIdFilter::getPlanIdFromArgsSysFilter(element.args()));
        }
    }

    // If no period template is found
    if (!reqGroupForPeriodTemplate.PeriodTemplate)
    {
        // Try to find period template through the coverage group on the item
        if (itemId)
        {
            reqSetupDim = ReqSetupDim::newInventTable(InventTable::find(itemId), callerInventDim.InventDimId);
            defaultPeriodTemplate = reqSetupDim.periodTemplateRecId();
        }
    }

    if (!defaultPeriodTemplate)
    {
        defaultPeriodTemplate = WorkPeriodTemplate::findDefault(CompanyInfo::findDataArea(curext()).RecId).RecId;
    }

    if (defaultPeriodTemplate)
    {
        element.parmPeriodTemplate(defaultPeriodTemplate);
    }

    inventDimFormSetupSalesLine = element.updateDesign(InventDimFormDesignUpdate::Init, #DemandSalesOrders, inventDimSalesLine_ds.name());
    inventDimFormSetupSalesLine = element.updateDesign(InventDimFormDesignUpdate::Init, #SupplyReturnSalesOrders, inventDimSalesLine_ds.name());
    inventDimFormSetupPurchLine = element.updateDesign(InventDimFormDesignUpdate::Init, #SupplyPurchaseOrders, inventDimPurchLine_ds.name());
    inventDimFormSetupPurchLine = element.updateDesign(InventDimFormDesignUpdate::Init, #DemandReturnPurchaseOrders, inventDimPurchLine_ds.name());
    inventDimFormSetupProdTable = element.updateDesign(InventDimFormDesignUpdate::Init, #SupplyProductionOrders, inventDimProdTable_ds.name());
    inventDimFormSetupInventTransferLine = element.updateDesign(InventDimFormDesignUpdate::Init, #SupplyTransferOrders, inventDimInventTransferLine_ds.name());
    inventDimFormSetupProductionKanban = element.updateDesign(InventDimFormDesignUpdate::Init, #SupplyProductionKanban, inventDimProductionKanban_ds.name());
    inventDimFormSetupKanbanTransfer = element.updateDesign(InventDimFormDesignUpdate::Init, #SupplyWithdrawalKanbans, inventDimKanbanTransfer_ds.name());

    inventDimFormSetupForecastInvent = element.updateDesignForNoMovement(InventDimFormDesignUpdate::Init);
    inventDimFormSetupReqPO = element.updateDesignForNoMovement(InventDimFormDesignUpdate::Init);
}

public void initEditControls()
{
    #define.controlDisplayLength(20)

    var sysDictEnum = new SysDictEnum(enumNum(FormStringControlExtendedStyle));

    // Add under minimum inventory level control
    underMinimumInventoryLevel = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_UnderMinimumInventoryLevelControl');
    underMinimumInventoryLevel.displayLengthValue(#controlDisplayLength);
    underMinimumInventoryLevel.viewEditMode(ViewEditMode::Edit);
    underMinimumInventoryLevel.allowEdit(false);

    // Add over maximum inventory level control
    overMaximumInventoryLevel = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_OverMaximumInventoryLevelControl');
    overMaximumInventoryLevel.displayLengthValue(#controlDisplayLength);
    overMaximumInventoryLevel.viewEditMode(ViewEditMode::Edit);
    overMaximumInventoryLevel.allowEdit(false);

    // Add header control
    headerControl = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_HeaderControl');
    headerControl.displayLengthValue(#controlDisplayLength);
    headerControl.viewEditMode(ViewEditMode::Edit);
    headerControl.allowEdit(false);

    // Add low delay control
    lowDelayControl = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_LowDelayControl');
    lowDelayControl.displayLengthValue(#controlDisplayLength);
    lowDelayControl.viewEditMode(ViewEditMode::Edit);
    lowDelayControl.allowEdit(false);
    lowDelayControl.extendedStyle(sysDictEnum.index2Symbol(FormStringControlExtendedStyle::planning_fieldFrozen));

    // Add medium delay control
    mediumDelayControl = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_MediumDelayControl');
    mediumDelayControl.displayLengthValue(#controlDisplayLength);
    mediumDelayControl.viewEditMode(ViewEditMode::Edit);
    mediumDelayControl.allowEdit(false);
    mediumDelayControl.extendedStyle(sysDictEnum.index2Symbol(FormStringControlExtendedStyle::entityStatus_warning));

    // Add high delay control
    highDelayControl = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_HighDelayControl');
    highDelayControl.displayLengthValue(#controlDisplayLength);
    highDelayControl.viewEditMode(ViewEditMode::Edit);
    highDelayControl.allowEdit(false);
    highDelayControl.extendedStyle(sysDictEnum.index2Symbol(FormStringControlExtendedStyle::entityStatus_critical));

    // Add default:
    defaultControl = supplyDemandOverviewTable.addControl(FormControlType::String, 'Table_DefaultControl');
    defaultControl.displayLengthValue(#controlDisplayLength);
    defaultControl.viewEditMode(ViewEditMode::Edit);
    defaultControl.allowEdit(false);
    defaultControl.extendedStyle(sysDictEnum.index2Symbol(FormStringControlExtendedStyle::entityStatus_info));
}

public Object inventDimSetupObject()
{
    switch (currentPreview)
    {
        case #DemandSalesOrders:
            return inventDimFormSetupSalesLine;

        case #SupplyPurchaseOrders:
            return inventDimFormSetupPurchLine;

        case #DemandForecast,
            #SupplyPlannedProductionOrdersForecast,
            #SupplyPmfPlannedBatchOrdersForecast,
            #SupplyPlannedKanbanForecast,
            #SupplyPlannedPurchaseOrdersForecast,
            #SupplyTransferPlannedOrdersForecast:
            return inventDimFormSetupForecastInvent;

        case #SupplyProductionOrders:
            return inventDimFormSetupProdTable;

        case #SupplyPlannedProductionOrdersTotal,
            #SupplyPmfPlannedBatchOrdersTotal,
            #SupplyPlannedPurchaseOrdersTotal,
            #SupplyTransferPlannedOrdersTotal,
            #SupplyPlannedKanbanTotal,
            #SupplyTransferPlannedKanbansTotal:
            return inventDimFormSetupReqPO;

        case #SupplyTransferOrders:
            return inventDimFormSetupInventTransferLine;

        case #SupplyProductionKanban:
            return inventDimFormSetupProductionKanban;

        case #SupplyWithdrawalKanbans:
            return inventDimFormSetupKanbanTransfer;
    }

    return null;
}

public void inventForecastPreview(
    int                 _currentSelectionPeriod,
    int                 _currentSelection,
    ForecastTableType   _forecastTableType)
{
    QueryBuildRange     qrItemId;
    InventDimParm       inventDimParm;
    TransDate           fromDate = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate           toDate   = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);
    InventDim           filterInventDim = element.parmInventDim();

    // Activate preview tab
    forecastInventPreviewTab.visible(true);
    forecastInventPreviewTab.caption(strFmt("%1 - %2", "@SYS16157", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));

    SysQuery::findOrCreateRange(qbdsForecastInvent, fieldNum(ForecastInvent, TableType)).value(queryValue(_forecastTableType));
    SysQuery::findOrCreateRange(qbdsForecastInvent, fieldNum(ForecastInvent, DateBudget)).value(queryRange(fromDate, toDate));
    SysQuery::findOrCreateRange(qbdsForecastModel, fieldNum(ForecastModel, ModelId)).value(queryValue(ReqPlan::findReqPlanId(reqPlanId).ForecastModelId));

    // Set filter for item allocation key
    qrItemId = SysQuery::findOrCreateRange(qbdsForecastInvent, fieldNum(ForecastInvent, ItemId));

    if (element.parmForecastItemAllocationId())
    {
        qbdsForecastAllocationLine.enabled(true);
        SysQuery::findOrCreateRange(qbdsForecastAllocationLine, fieldNum(ForecastItemAllocationLine, AllocationId)).value(element.parmForecastItemAllocationId());
        qrItemId.value(SysQuery::valueUnlimited());
    }
    else
    {
        qbdsForecastAllocationLine.enabled(false);
        qrItemId.value(queryValue(element.parmItemId()));
    }

    inventDimParm.initFromInventDim(filterInventDim);
    inventDimParm.queryAddRangeValue(qbdsForecastReqTransDim, filterInventDim);

    // Query data if fast tab is expanded
    if (forecastInventPreviewTab.isExpanded())
    {
        buttonForecastSupply.visible(_forecastTableType == ForecastTableType::Supply);
        buttonForecastDemand.visible(_forecastTableType == ForecastTableType::Demand);
        forecastInvent_ds.executeQuery();
        isPreviewUpdated = true;
    }
}

public void inventoryLevelMessage()
{
    Qty periodEndInventory;

    periodEndInventory = supplyDemandOverviewTable.cell(supplyDemandOverviewTable.column(), reqSupplyDemandScheduleControl.getRowNumberFromTableNumber(#PeriodEndInventory)).data();

    if (minimumInventoryLevel && periodEndInventory < minimumInventoryLevel)
    {
        // The end inventory level for the period is lower than the defined minimum inventory level (%1)
        inventoryLevelWarningMessage.text(strFmt("@SYS324558", minimumInventoryLevel));
        inventoryLevelWarningMessage.visible(true);
    }
    else
    if (maximumInventoryLevel && periodEndInventory > maximumInventoryLevel)
    {
        // The end inventory level for the period is higher than the defined maximum inventory level (%1)
        inventoryLevelWarningMessage.text(strFmt("@SYS324559", maximumInventoryLevel));
        inventoryLevelWarningMessage.visible(true);
    }
    else
    {
        inventoryLevelWarningMessage.visible(false);
    }
}

public void openFilterForm()
{
    reqSupplyDemandScheduleFilters.clicked();
}

public ForecastItemAllocationId parmForecastItemAllocationId(ForecastItemAllocationId _itemAllocationKey = itemAllocationKey)
{
    if (itemAllocationKey != _itemAllocationKey)
    {
        if (_itemAllocationKey)
        {
            itemId = '';
            reqSupplyDemandScheduleModel.parmItemId('');
        }

        minimumInventoryLevel = 0;
        maximumInventoryLevel = 0;

        reqSupplyDemandScheduleModel.parmForecastItemAllocationId(_itemAllocationKey);
    }

    itemAllocationKey = _itemAllocationKey;

    return itemAllocationKey;
}

public InventDim parmInventDim(InventDim _inventDim = inventDim)
{
    InventDimParm   inventDimParm;

    if (inventDim != _inventDim)
    {
        reqSupplyDemandScheduleModel.parmInventDim(_inventDim);

        inventDimParm.initFromInventDim(_inventDim);
        reqSupplyDemandScheduleModel.parmInventDimParm(inventDimParm);
    }

    inventDim = _inventDim;

    return inventDim;
}

public ItemId parmItemId(ItemId _itemId = itemId)
{
        if (itemId != _itemId)
        {
            reqSupplyDemandScheduleModel.parmItemId(_itemId);

            if (_itemId)
            {
                reqSupplyDemandScheduleModel.parmForecastItemAllocationId('');
                itemAllocationKey = '';
            }
        }

        itemId = _itemId;

    return itemId;
}

public RefRecId parmPeriodTemplate(RefRecId _periodTemplate = periodTemplate)
{
    if (_periodTemplate != periodTemplate)
    {
        reqSupplyDemandScheduleModel.parmPeriodTemplate(_periodTemplate);
    }

    periodTemplate = _periodTemplate;

    return periodTemplate;
}

public ReqPlanId parmReqPlanId(ReqPlanId _reqPlanId = reqPlanId)
{
    if (_reqPlanId != reqPlanId)
    {
        reqSupplyDemandScheduleModel.parmReqPlanId(_reqPlanId);
    }

    reqPlanId = _reqPlanId;

    reqTransForm.parmReqPlanId(_reqPlanId);

    return reqPlanId;
}

public RecId parmReqPlanVersionRecId(RecId _reqPlanVersionRecId = reqPlanVersionRecId)
{
    reqPlanVersionRecId = _reqPlanVersionRecId;

    return reqPlanVersionRecId;
}

public void recalculate()
{
    reqSupplyDemandScheduleModel.buildQuery();

    reqSupplyDemandScheduleModel.calcPeriods();

    tableData = reqSupplyDemandScheduleModel.calculatedQuantities();

    // Get show/hidden state of the rows
    reqSupplyDemandScheduleControl.setTableRowsVisible(reqSupplyDemandScheduleModel.typeVisible());
}

protected void recalculateDelays(boolean forceCalculation = false)
{
    if (conlen(delays) > 0 || forceCalculation)
    {
        delays = reqSupplyDemandScheduleModel.findDelays();
    }
}

public boolean registerActionsPartForUpdate(Object _part)
{
    actionsPart = _part;

    return true;
}

public boolean registerFuturesPartForUpdate(Object _part)
{
    futuresPart = _part;

    return true;
}

public boolean registerItemDetailsPartForUpdate(Object _part)
{
    itemDetailsPart = _part;

    return true;
}

public void renderData(boolean _clearOldValues)
{
    int         rowRenderingCounter;
    int         rowPeriodsCounter;
    ReqQty      periodQuantity;
    container   tableRowPeriodValues;
    int         numberOfColumnsToRender;

    for (rowRenderingCounter = 1; rowRenderingCounter <= conLen(renderRows); rowRenderingCounter = rowRenderingCounter + 2)
    {
        tableRowPeriodValues = conPeek(tableData, conPeek(renderRows, rowRenderingCounter + 1));

        // Are there any values to be shown for this row?
        if (conLen(tableRowPeriodValues) > 0)
        {
            if (_clearOldValues)
            {
                numberOfColumnsToRender = supplyDemandOverviewTable.columns();
            }
            else
            {
                numberOfColumnsToRender = conLen(tableRowPeriodValues);
            }

            // Traverse the periods for the row
            for (rowPeriodsCounter = 1; rowPeriodsCounter <= numberOfColumnsToRender; rowPeriodsCounter++)
            {
                // Set value in the appropriate cell if it is not zero
                periodQuantity = conPeek(tableRowPeriodValues, rowPeriodsCounter);
                if (periodQuantity)
                {
                    supplyDemandOverviewTable.cell(rowPeriodsCounter, conPeek(renderRows, rowRenderingCounter)).data(System.String::Format(numberFormatInfo, "{0:N}", periodQuantity));
                }
                else
                if (_clearOldValues)
                {
                    supplyDemandOverviewTable.cell(rowPeriodsCounter, conPeek(renderRows, rowRenderingCounter)).data('');
                }
            }
        }
    }

    supplyDemandOverviewTable.update();
}

ReqTransForm reqTransForm()
{
    return reqTransForm;
}

public void resetFocus()
{
    supplyDemandOverviewTable.column(1);
    supplyDemandOverviewTable.row(1);

    currentRow      = 1;
    currentColumn   = 1;

    supplyDemandOverviewTable.leave();
}

public void run()
{
    super();

    element.eventOpenForm();
}

public void setInventDimParameters(Common _buffer)
{
    inventDim = _buffer;
}

private void setInventDimRanges(Map _inventDimRangesMap)
{
    InventDim filterInventDim = element.parmInventDim();

    MapEnumerator inventDimRanges = _inventDimRangesMap.getEnumerator();
    while (inventDimRanges.moveNext())
    {
        FieldId inventDimFieldId = inventDimRanges.currentKey();
        QueryBuildRange inventDimFieldRange = inventDimRanges.currentValue();

        if (filterInventDim.(inventDimFieldId))
        {
            inventDimFieldRange.enabled(true);
            inventDimFieldRange.value(queryValue(filterInventDim.(inventDimFieldId)));
        }
        else
        {
            inventDimFieldRange.enabled(false);
        }
    }
}

private boolean isItemIdOrAllocationIdSet()
{
    return element.parmItemId() || element.parmForecastItemAllocationId();
}

public void setInventoryMinMaxLevels()
{
    ReqSetupDim     reqSetupDim;

    if (itemId)
    {
        reqSetupDim = ReqSetupDim::newInventTable(InventTable::find(itemId), InventDim::findOrCreate(inventDim).InventDimId);

        minimumInventoryLevel = reqSetupDim.minInventOnHand();
        maximumInventoryLevel = reqSetupDim.maxInventOnHand();
    }
}

void updateInventoryProfileGraph()
{
    int             i;
    container       tableRowPeriodValues;

    // Clear existing records
    delete_from inventoryProfileChartTmp;

    // Get period supply
    tableRowPeriodValues = conPeek(tableData, #PeriodEndInventory);

    for (i = 1; i <= conLen(periodTemplatePeriods); i++)
    {
        inventoryProfileChartTmp.clear();
        inventoryProfileChartTmp.PeriodNumber = i;
        inventoryProfileChartTmp.PeriodName = strFmt("@SYS70938", conPeek(conPeek(periodTemplatePeriods, i), 3), i);
        inventoryProfileChartTmp.Qty = conPeek(tableRowPeriodValues, i);
        inventoryProfileChartTmp.insert();
    }

    inventoryProfileChartTmp_ds.executeQuery();
    supplyProfileChart.update();
}

void updateSupplyProfileGraph()
{
    int             i;
    container       tableRowPeriodValues;

    // Clear existing records
    delete_from supplyProfileChartTmp;

    // Get period supply
    tableRowPeriodValues = conPeek(tableData, reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(supplyDemandOverviewTable.row()));

    for (i = 1; i <= conLen(periodTemplatePeriods); i++)
    {
        supplyProfileChartTmp.clear();
        supplyProfileChartTmp.PeriodNumber = i;
        supplyProfileChartTmp.PeriodName = strFmt("@SYS70938", conPeek(conPeek(periodTemplatePeriods, i), 3), i);
        supplyProfileChartTmp.Qty = conPeek(tableRowPeriodValues, i);
        supplyProfileChartTmp.insert();
    }

    supplyProfileChartTmp_ds.executeQuery();
    inventoryProfileChart.update();
}

public void supplyDemandTransferOrdersPreview(int _currentSelectionPeriod, int _currentSelection)
{
    #define.transferOrdersTotal(1)
    #define.transderOrdersTotalQuantity(2)

    TransDate fromDate  = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate toDate    = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);

    // Activate preview tab
    inventTransferLinePreviewTab.visible(true);

    // Transfer orders
    inventTransferLinePreviewTab.caption(strFmt("%1 - %2", "@SYS78286", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));

    // Set filter for item allocaion key
    if (element.parmForecastItemAllocationId())
    {
        datasourceInventTransferAllocationKey.enabled(true);
        rangeInventTransferAllocationKey.value(queryValue(element.parmForecastItemAllocationId()));
        rangeInventTransferItemId.enabled(false);
    }
    // Set filter for item
    else
    {
        datasourceInventTransferAllocationKey.enabled(false);
        rangeInventTransferAllocationKey.value(SysQuery::valueUnlimited());
        rangeInventTransferItemId.enabled(true);
        rangeInventTransferItemId.value(queryValue(element.parmItemId()));
    }

    // Set filter for inventory dimensions
    if (_currentSelection == #DemandTransferOrders)
    {
        // If a transfer order demand use the from location for filtering
        element.setInventDimRanges(inventTransferInventDimFromQueryRanges);

        QueryBuildRange qbrInventDimToSiteId = inventTransferInventDimToQueryRanges.lookup(fieldNum(InventDim, InventSiteId));
        QueryBuildRange qbrInventDimToWarehouseId = inventTransferInventDimToQueryRanges.lookup(fieldNum(InventDim, InventLocationId));
        qbrInventDimToSiteId.value(SysQuery::valueUnlimited());
        qbrInventDimToWarehouseId.value(SysQuery::valueUnlimited());
    }
    else
    if (_currentSelection == #SupplyTransferOrders)
    {
        // If a transfer order supply use the to location for filtering
        element.setInventDimRanges(inventTransferInventDimToQueryRanges);

        QueryBuildRange qbrInventDimToSiteId = inventTransferInventDimFromQueryRanges.lookup(fieldNum(InventDim, InventSiteId));
        QueryBuildRange qbrInventDimToWarehouseId = inventTransferInventDimFromQueryRanges.lookup(fieldNum(InventDim, InventLocationId));
        qbrInventDimToSiteId.value(SysQuery::valueUnlimited());
        qbrInventDimToWarehouseId.value(SysQuery::valueUnlimited());
    }

    // Filter based on the selected period
    if (_currentSelection == #DemandTransferOrders)
    {
        // If a transfer order demand use the from ship date for filtering
        rangeInventTransferShipDate.value(queryRange(fromDate, toDate));
        rangeInventTransferReceiveDate.value(SysQuery::valueUnlimited());
    }
    else
    if (_currentSelection == #SupplyTransferOrders)
    {
        // If a transfer order supply use the from receive date for filtering
        rangeInventTransferReceiveDate.value(queryRange(fromDate, toDate));
        rangeInventTransferShipDate.value(SysQuery::valueUnlimited());
    }
    if (inventTransferLinePreviewTab.isExpanded())
    {
        inventTransferTable_ds.executeQuery();
        isPreviewUpdated = true;
    }

    summaryData = reqSupplyDemandScheduleModel.transferOrdersPreview(fromDate,
                                                                     toDate,
                                                                     _currentSelection == #DemandTransferOrders ? InventDirection::Issue : InventDirection::Receipt);

    groupNoSummary.visible(false);
    groupTransferOrdersSummary.visible(true);

    // Update the form control with the summary data
    transferOrdersTotal.realValue(conPeek(summaryData, #transferOrdersTotal));
    transderOrdersTotalQuantity.realValue(conPeek(summaryData, #transderOrdersTotalQuantity));

    summaryTab.visible(true);
}

public void supplyKanbanProductionPreview(int _currentSelectionPeriod)
{
    TransDate   fromDate        = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate   toDate          = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);
    utcdatetime fromDateTime;
    utcdatetime toDateTime;

    if (toDate == maxDate())
    {
        toDate = dateMax() - 2;
    }

    fromDateTime    = DateTimeUtil::newDateTime(fromDate, 0);
    toDateTime      = DateTimeUtil::newDateTime(toDate + 1, 0);

    // Activate preview tab
    kanbanProductionPreview.visible(true);

    // Production kanbans
    kanbanProductionPreview.caption(strFmt("%1 - %2", "@SYS318042", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));

    // Set filter for item allocaion key
    if (element.parmForecastItemAllocationId())
    {
        datasourceKanbanProductionAllocationKey.enabled(true);
        rangeKanbanProductionAllocationKey.value(element.parmForecastItemAllocationId());
        rangeKanbanProductionItemId.enabled(false);
    }
    // Set filter for item
    else
    {
        datasourceKanbanProductionAllocationKey.enabled(false);
        rangeKanbanProductionAllocationKey.value('');
        rangeKanbanProductionItemId.enabled(true);
        rangeKanbanProductionItemId.value(queryValue(element.parmItemId()));
    }

    // Set filter for inventory dimensions
    element.setInventDimRanges(kanbanProductionInventDimQueryRanges);

    // Filter based on the selected period
    rangeKanbanProductionDueDate.value(strFmt('(((%1.%2 == %6) && (%1.%4 >= %3) && (%1.%4 < %5))))',
        kanbanProduction_ds.name(),                                                                                           // 1
    fieldStr(Kanban, ExpectedDateTime),                                                                                   // 2
    fromDate != dateNull() ? DateTimeUtil::toStr(fromDateTime) : KanbanOverviewQueryBuilderCriteria::dateTimeNull2Str(),  // 3
    fieldStr(Kanban, DueDateTime),                                                                                        // 4
    DateTimeUtil::toStr(toDateTime),                                                                                      // 5
    KanbanOverviewQueryBuilderCriteria::dateTimeNull2Str()));                                                             // 6

    rangeKanbanProductionExpectedDate.value(strFmt('(((%1.%2 != %6) && (%1.%2 >= %3) && (%1.%2 < %5)))',
        kanbanProduction_ds.name(),                                                                                           // 1
    fieldStr(Kanban, ExpectedDateTime),                                                                                   // 2
    fromDate != dateNull() ? DateTimeUtil::toStr(fromDateTime) : KanbanOverviewQueryBuilderCriteria::dateTimeNull2Str(),  // 3
    fieldStr(Kanban, DueDateTime),                                                                                        // 4
    DateTimeUtil::toStr(toDateTime),                                                                                      // 5
    KanbanOverviewQueryBuilderCriteria::dateTimeNull2Str()));                                                             // 6

    if (kanbanProductionPreview.isExpanded())
    {
        kanbanProduction_ds.executeQuery();
        isPreviewUpdated = true;
    }

    summaryData = reqSupplyDemandScheduleModel.kanbansPreview(fromDate, toDate, LeanKanbanType::Manufacturing);

    groupNoSummary.visible(false);
    groupProductionKanbansSummary.visible(true);

    // Update the form control with the summary data
    productionKanbansTotal.realValue(conPeek(summaryData, 1));
    productionKanbansTotalDueQuantity.realValue(conPeek(summaryData, 2));
    productionKanbansPlannedForPeriod.realValue(conPeek(summaryData, 3));
    productionKanbansTotalPlannedQuantity.realValue(conPeek(summaryData, 4));

    summaryTab.visible(true);
}

