public void supplyPlannedOrdersPreview(int _currentSelectionPeriod, int _currentSelection)
{
    #define.plannedOrders(1)
    #define.plannedOrdersQty(2)

    TransDate fromDate  = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate toDate    = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);

    reqSupplyDemandScheduleModel.parmCurrentSelectionRow(_currentSelection);

    // Activate preview tab
    reqPOPreviewTab.visible(true);

    // Set filter for item allocation key
    if (element.parmForecastItemAllocationId())
    {
        datasourceReqPOAllocationKey.enabled(true);
        rangeReqPOAllocationKey.value(element.parmForecastItemAllocationId());
        rangeReqPOItemId.enabled(false);
    }
    // Set filter for item
    else
    {
        datasourceReqPOAllocationKey.enabled(false);
        rangeReqPOAllocationKey.value('');
        rangeReqPOItemId.enabled(true);
        rangeReqPOItemId.value(queryValue(element.parmItemId()));
    }

    // Set filter for inventory dimensions
    rangeReqPOInventDimSiteId.enabled(true);
    rangeReqPOInventDimLocationId.enabled(true);
    datasourceTransferDemand.enabled(false);
    element.setInventDimRanges(reqPOInventDimQueryRanges);

    if (   _currentSelection == #DemandPlannedTransferOrders
        && element.reqSupplyDemandScheduleModel.isSiteOrWarehouseFilterSet())
    {
        // Use different site/warehouse ranges to show demand.
        rangeReqPOInventDimSiteId.enabled(false);
        rangeReqPOInventDimLocationId.enabled(false);
        datasourceTransferDemand.enabled(true);
        element.setInventDimRanges(reqPOInventDimPlannedTransferSiteWarehouseQueryRanges);
    }

    // Filter based on the selected period
    if (_currentSelection == #DemandPlannedTransferOrders && ReqSupplyDemandScheduleDemandPlannedTransferOrdersToggle::instance().isEnabled())
    {
        // If a transfer order demand then the order date for filtering should be used
        rangeReqPODateOrder.value(queryRange(fromDate, toDate));
        rangeReqPODate.value(SysQuery::valueUnlimited());
    }
    else
    {
        rangeReqPODateOrder.value(SysQuery::valueUnlimited());
        rangeReqPODate.value(queryRange(fromDate, toDate));
    }

    // Filter based on the selected plan
    str reqPlanActiveVersionId = queryValue(this.selectedPlanVersionRecId());
    rangeReqPOPlanVersion.value(reqPlanActiveVersionId);
    rangeReqPOReqTransPlanVersion.value(reqPlanActiveVersionId);

    reqPODetails.visible(true);
    reqTransPoTransfer.visible(false);

    if (_currentSelection == #SupplyPlannedProductionOrdersTotal)
    {
        // Planned production orders
        reqPOPreviewTab.caption(strFmt("%1 - %2", "@SYS12742", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));
        rangeReqPOType.value(queryValue(ReqRefType::BOMPlannedOrder));
        rangeReqPOSubClassification.value(queryValue(SupplyDemandSubClassification::None));
        reqPOWrkCtrCapRes.visible(true);
        buttonReqTransPoMarkFirm.visible(true);
        reqTransPoMarkApprove.visible(true);
        reqTransPoMarkChangeStatus.visible(true);
        summaryData = reqSupplyDemandScheduleModel.plannedOrdersPreview(fromDate, toDate, ReqRefType::BOMPlannedOrder, SupplyDemandSubClassification::None);
    }
    else
    if (_currentSelection == #SupplyPmfPlannedBatchOrdersTotal)
    {
        // Planned batch orders
        reqPOPreviewTab.caption(strFmt("%1 - %2", "@PRO479", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));
        rangeReqPOType.value(queryValue(ReqRefType::PmfPlannedProdBatch));
        rangeReqPOSubClassification.value(queryValue(SupplyDemandSubClassification::None));
        reqPOWrkCtrCapRes.visible(true);
        buttonReqTransPoMarkFirm.visible(true);
        reqTransPoMarkApprove.visible(true);
        reqTransPoMarkChangeStatus.visible(true);
        summaryData = reqSupplyDemandScheduleModel.plannedOrdersPreview(fromDate, toDate, ReqRefType::PmfPlannedProdBatch, SupplyDemandSubClassification::None);
    }
    else
    if (_currentSelection == #SupplyPlannedPurchaseOrdersTotal)
    {
        // Planned purchase orders
        reqPOPreviewTab.caption(strFmt("%1 - %2", "@SYS5151", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));
        rangeReqPOType.value(queryValue(ReqRefType::ItemPlannedOrder));
        rangeReqPOSubClassification.value(queryValue(SupplyDemandSubClassification::None));
        reqPOWrkCtrCapRes.visible(false);
        buttonReqTransPoMarkFirm.visible(true);
        reqTransPoMarkApprove.visible(true);
        reqTransPoMarkChangeStatus.visible(true);
        summaryData = reqSupplyDemandScheduleModel.plannedOrdersPreview(fromDate, toDate, ReqRefType::ItemPlannedOrder, SupplyDemandSubClassification::None);
    }
    else
    if (_currentSelection == #SupplyTransferPlannedOrdersTotal || _currentSelection == #DemandPlannedTransferOrders)
    {
        reqPODetails.visible(false);
        reqTransPoTransfer.visible(true);
        // Planned transfer orders
        reqPOPreviewTab.caption(strFmt("%1 - %2", "@SYS318035", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));
        rangeReqPOType.value(queryValue(ReqRefType::TransferPlannedOrder));
        rangeReqPOSubClassification.value(queryValue(SupplyDemandSubClassification::None));
        reqPOWrkCtrCapRes.visible(false);
        buttonReqTransPoMarkFirm.visible(true);
        reqTransPoMarkApprove.visible(true);
        reqTransPoMarkChangeStatus.visible(true);
        summaryData = reqSupplyDemandScheduleModel.plannedOrdersPreview(fromDate, toDate, ReqRefType::TransferPlannedOrder, SupplyDemandSubClassification::None);
    }
    else
    if (_currentSelection == #SupplyPlannedKanbanTotal)
    {
        // Planned production kanbans
        reqPOPreviewTab.caption(strFmt("%1 - %2", "@SYS318036", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));
        rangeReqPOType.value(queryValue(ReqRefType::PlannedKanban));
        rangeReqPOSubClassification.value(queryValue(SupplyDemandSubClassification::ProductionKanban));
        reqPOWrkCtrCapRes.visible(false);
        buttonReqTransPoMarkFirm.visible(true);
        reqTransPoMarkApprove.visible(true);
        reqTransPoMarkChangeStatus.visible(true);
        summaryData = reqSupplyDemandScheduleModel.plannedOrdersPreview(fromDate, toDate, ReqRefType::PlannedKanban, SupplyDemandSubClassification::ProductionKanban);
    }
    else
    if (_currentSelection == #SupplyTransferPlannedKanbansTotal)
    {
        // Planned withdrawal kanbans
        reqPOPreviewTab.caption(strFmt("%1 - %2", "@SYS318037", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));
        rangeReqPOType.value(queryValue(ReqRefType::PlannedKanban));
        rangeReqPOSubClassification.value(queryValue(SupplyDemandSubClassification::WithdrawalKanban));
        reqPOWrkCtrCapRes.visible(false);
        buttonReqTransPoMarkFirm.visible(false);
        reqTransPoMarkApprove.visible(false);
        reqTransPoMarkChangeStatus.visible(false);
        summaryData = reqSupplyDemandScheduleModel.plannedOrdersPreview(fromDate, toDate, ReqRefType::PlannedKanban, SupplyDemandSubClassification::WithdrawalKanban);
    }

    if (reqPOPreviewTab.isExpanded())
    {
        reqPO_ds.executeQuery();
        isPreviewUpdated = true;
    }

    groupNoSummary.visible(false);
    groupPlannedOrdersSummary.visible(true);

    // Update the form control with the summary data
    plannedOrders.realValue(conPeek(summaryData, #plannedOrders));
    plannedOrdersQty.realValue(conPeek(summaryData, #plannedOrdersQty));

    summaryTab.visible(true);
}

public void supplyProductionOrdersPreview(int _currentSelectionPeriod)
{
    #define.productionOrdersScheduled(1)
    #define.productionOrdersTotalQtyScheduled(2)
    #define.productionOrdersRequested(3)
    #define.productionOrdersTotalQtyRequested(4)

    TransDate   fromDate  = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate   toDate    = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);

    if (toDate == maxDate())
    {
        toDate = dateMax() - 2;
    }

    // Activate preview tab
    prodTablePreviewTab.visible(true);
    // Production orders
    prodTablePreviewTab.caption(strFmt("%1 - %2", "@SYS9747", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));

    // Set filter for item allocaion key
    if (element.parmForecastItemAllocationId())
    {
        datasourceProdTableAllocationKey.enabled(true);
        rangeProdTableAllocationKey.value(element.parmForecastItemAllocationId());
        rangeProdTableItemId.enabled(false);
    }
    // Set filter for item
    else
    {
        datasourceProdTableAllocationKey.enabled(false);
        rangeProdTableAllocationKey.value('');
        rangeProdTableItemId.enabled(true);
        rangeProdTableItemId.value(queryValue(element.parmItemId()));
    }

    // Set filter for inventory dimensions
    element.setInventDimRanges(prodTableInventDimQueryRanges);

    // Filter based on the selected period
    rangeProdTableDeliveryDate.value(strFmt('(((%1.%2 == %6) && (%1.%4 >= %3) && (%1.%4 <= %5))))',
        prodTable_ds.name(),                                                                                                  // 1
    fieldStr(ProdTable, SchedEnd),                                                                                        // 2
    date2StrXpp(fromDate),                                                                                                // 3
    fieldStr(ProdTable, DlvDate),                                                                                         // 4
    date2StrXpp(toDate),                                                                                                  // 5
    date2StrXpp(dateNull())));                                                                                            // 6

    rangeProdTableSchedEndDate.value(strFmt('(((%1.%2 != %6) && (%1.%2 >= %3) && (%1.%2 <= %5)))',
        prodTable_ds.name(),                                                                                                  // 1
    fieldStr(ProdTable, SchedEnd),                                                                                        // 2
    date2StrXpp(fromDate),                                                                                                // 3
    fieldStr(ProdTable, DlvDate),                                                                                         // 4
    date2StrXpp(toDate),                                                                                                  // 5
    date2StrXpp(dateNull())));                                                                                            // 6

    if (prodTablePreviewTab.isExpanded())
    {
        prodTable_ds.executeQuery();
        isPreviewUpdated = true;
    }

    summaryData = reqSupplyDemandScheduleModel.productionOrdersPreview(fromDate, toDate);

    groupNoSummary.visible(false);
    groupProdTableSummary.visible(true);

    // Update the form control with the summary data
    productionOrdersScheduled.realValue(conPeek(summaryData, #productionOrdersScheduled));
    productionOrdersTotalQtyScheduled.realValue(conPeek(summaryData, #productionOrdersTotalQtyScheduled));
    productionOrdersRequested.realValue(conPeek(summaryData, #productionOrdersRequested));
    productionOrdersTotalQtyRequested.realValue(conPeek(summaryData, #productionOrdersTotalQtyRequested));

    summaryTab.visible(true);
}

public void purchaseOrdersPreview(int _currentSelectionPeriod, int _currentSelection)
{
    const int PurchaseOrderLinesPosition = 1;
    const int PurchaseOrderTotalQuantityPosition = 2;
    const int PurchaseOrderTotalAmountPosition = 3;
    const int PurchaseOrderConfirmedLinesPosition = 4;
    const int PurchaseOrderConfirmedQuantityPosition = 5;

    // Activate preview tab
    purchPreviewTab.visible(true);
    // Purchase orders
    purchPreviewTab.caption(strFmt("%1 - %2", "@SYS115660", supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));

    // Set filter for item allocaion key
    if (element.parmForecastItemAllocationId())
    {
        datasourcePurchLineAllocationKey.enabled(true);
        rangePurchLineAllocationKey.value(element.parmForecastItemAllocationId());
        rangePurchLineItemId.enabled(false);
    }
    // Set filter for item
    else
    {
        datasourcePurchLineAllocationKey.enabled(false);
        rangePurchLineAllocationKey.value('');
        rangePurchLineItemId.enabled(true);
        rangePurchLineItemId.value(queryValue(element.parmItemId()));
    }

    // Set filter for inventory dimensions
    element.setInventDimRanges(purchLineInventDimQueryRanges);

    // Filter based on the selected period
    int dayOffset;
    TransDate   fromDate  = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate   toDate    = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);

    if (fromDate == dateNull())
    {
        dayOffset = 1;
    }

    str rangeStr = '(';
    rangeStr += strFmt('((%1.%2 >= %3) && (%1.%2 <= %4)) || ( ((%1.%6 >= %3) && (%1.%6 <= %4)) && (%1.%2 == %5))',
        rangePurchLineDate.dataSource().name(),
        fieldStr(PurchLine, ConfirmedDlv),
        date2StrXpp(fromDate + dayOffset),
        date2StrXpp(toDate),
        date2StrXpp(dateNull()),
        fieldStr(PurchLine, DeliveryDate)
        );

    if (_currentSelection == #DemandReturnPurchaseOrders)
    {
        rangeStr += strFmt(' && (%1.%2 < 0)', rangePurchLineDate.dataSource().name(), fieldStr(PurchLine, PurchQty));
    }
    else if (_currentSelection == #SupplyPurchaseOrders)
    {
        rangeStr += strFmt(' && (%1.%2 >= 0)', rangePurchLineDate.dataSource().name(), fieldStr(PurchLine, PurchQty));
    }
    rangeStr +=')';

    rangePurchLineDate.value(rangeStr);

    if (purchPreviewTab.isExpanded())
    {
        purchLine_ds.executeQuery();
        isPreviewUpdated = true;
    }

    summaryData = reqSupplyDemandScheduleModel.purchaseOrdersPreview(fromDate, toDate);

    groupNoSummary.visible(false);
    groupPurchaseOrdersSummary.visible(true);

    // Update the form control with the summary data
    purchaseOrderLines.realValue(conPeek(summaryData, PurchaseOrderLinesPosition));
    purchaseOrderTotalQuantity.realValue(conPeek(summaryData, PurchaseOrderTotalQuantityPosition));
    purchaseOrderTotalAmount.realValue(conPeek(summaryData, PurchaseOrderTotalAmountPosition));
    purchaseOrderConfirmedLines.realValue(conPeek(summaryData, PurchaseOrderConfirmedLinesPosition));
    purchaseOrderConfirmedQuantity.realValue(conPeek(summaryData, PurchaseOrderConfirmedQuantityPosition));

    summaryTab.visible(true);
}

public void updateButtons()
{
    int currentSelection = reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(supplyDemandOverviewTable.row());

    if (currentSelection == #SupplyPlannedProductionOrdersTotal ||
        currentSelection == #SupplyPmfPlannedBatchOrdersTotal ||
        currentSelection == #SupplyPlannedProductionOrdersForecast ||
        currentSelection == #SupplyPmfPlannedBatchOrdersForecast ||
        currentSelection == #SupplyPlannedProductionOrders ||
        currentSelection == #SupplyPmfPlannedBatchOrders ||
        currentSelection == #SupplyPlannedPurchaseOrdersTotal ||
        currentSelection == #SupplyPlannedPurchaseOrders ||
        currentSelection == #SupplyPlannedPurchaseOrdersForecast ||
        currentSelection == #SupplyTransferPlannedKanbansTotal ||
        currentSelection == #SupplyTransferPlannedKanbansForecast ||
        currentSelection == #SupplyTransferPlannedKanbans ||
        currentSelection == #SupplyTransferPlannedKanbansTotal ||
        currentSelection == #SupplyTransferPlannedKanbansForecast ||
        currentSelection == #SupplyTransferPlannedKanbans ||
        currentSelection == #SupplyPlannedKanbanTotal ||
        currentSelection == #SupplyPlannedKanbanForecast ||
        currentSelection == #SupplyPlannedKanban)
    {
        reqPlannedOrderLeveling.enabled(true);
    }
    else
    {
        reqPlannedOrderLeveling.enabled(false);
    }

    kanbansCreate.enabled(itemId != '');
}

public InventDimCtrl_Frm_Mov updateDesign(InventDimFormDesignUpdate _mode,
                                          int                       _previewType = currentPreview,
                                          str                       _inventDimDatasourceName = '')
{
    InventMovement          movement;
    Set                     validInventDimDSset;
    InventDimCtrl_Frm_Mov   inventDimSetup;

    movement = this.initializeInventMovement(_previewType);

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimSetup)
            {
                validInventDimDSset = new Set(Types::String);
                validInventDimDSset.add(_inventDimDatasourceName);
                inventDimSetup  = InventDimCtrl_Frm_Mov::newFromFormMovement(element, movement);
            }
        // Fall through

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::FieldChange:
            if (inventDimSetup)
            {
                inventDimSetup.formActiveSetup(movement.inventDimGroupSetup(), movement);
                inventDimSetup.formSetControls(_mode == InventDimFormDesignUpdate::Active);
            }
            break;

        default:
            // Incorrect parameter for the function %1
            throw error(strFmt("@SYS54195", funcName()));
    }

    return inventDimSetup;
}

/// <summary>
///		Instantiates an instance of the <c>InventMovement</c> class.
/// </summary>
/// <param name = "_previewType">
///		A preview type.
/// </param>
/// <returns>
///		An <c>InventMovement</c> instance.
/// </returns>
protected InventMovement initializeInventMovement(int _previewType)
{
    InventMovement movement;

    /* For following types we should use updateDesignForNoMovement
    #DemandForecast,
    #SupplyPlannedProductionOrdersForecast,
    #SupplyPmfPlannedBatchOrdersForecast,
    #SupplyPlannedKanbanForecast,
    #SupplyPlannedPurchaseOrdersForecast,
    #SupplyTransferPlannedOrdersForecast,
    #SupplyPlannedProductionOrdersTotal,
    #SupplyPmfPlannedBatchOrdersTotal,
    #SupplyPlannedPurchaseOrdersTotal,
    #SupplyTransferPlannedOrdersTotal,
    #SupplyPlannedKanbanTotal,
    #SupplyTransferPlannedKanbansTotal:*/
    switch (_previewType)
    {
        case #DemandSalesOrders,
            #SupplyReturnSalesOrders:
            movement = InventMovement::construct(salesLine);
            break;

        case #SupplyPurchaseOrders,
            #DemandReturnPurchaseOrders:
            movement = InventMovement::construct(purchLine);
            break;

        case #SupplyProductionOrders:
            movement = InventMovement::construct(prodTable);
            break;

        case #SupplyTransferOrders,
            #DemandTransferOrders:
            movement = InventMovement::construct(inventTransferLine);
            break;

        case #SupplyProductionKanban:
            movement = InventMovement::construct(kanbanProduction);
            break;

        case #SupplyWithdrawalKanbans:
            movement = InventMovement::construct(kanbanTransfer);
            break;

        case #DemandForecast,
            #SupplyPlannedProductionOrdersForecast,
            #SupplyPmfPlannedBatchOrdersForecast,
            #SupplyPlannedKanbanForecast,
            #SupplyPlannedPurchaseOrdersForecast,
            #SupplyTransferPlannedOrdersForecast,
            #SupplyPlannedProductionOrdersTotal,
            #SupplyPmfPlannedBatchOrdersTotal,
            #SupplyPlannedPurchaseOrdersTotal,
            #SupplyTransferPlannedOrdersTotal,
            #SupplyPlannedKanbanTotal,
            #SupplyTransferPlannedKanbansTotal:
            // Not supported
            throw error("@SYS91571");
    }

    return movement;
}

public InventDimCtrl_Frm updateDesignForNoMovement(InventDimFormDesignUpdate _mode)
{
    InventDimCtrl_Frm inventDimSetup = InventDimCtrl::newFromForm(element);

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            break;
        case InventDimFormDesignUpdate::Active,
         InventDimFormDesignUpdate::FieldChange:
        if (inventDimSetup)
            {
                inventDimSetup.formSetControls(true);
            }
            break;

        default:
            // Incorrect parameter for the function %1
            throw error(strFmt("@SYS54195", funcName()));
    }

    return inventDimSetup;
}

public void updateInfoParts()
{
    if (futuresPart)
    {
        futuresPart.updateRanges();
    }

    if (actionsPart)
    {
        actionsPart.updateRanges();
    }
}

public void updatePreview()
{
    int currentSelection        = reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(supplyDemandOverviewTable.row());
    int currentSelectionPeriod  = supplyDemandOverviewTable.column();

    // Only refresh preview if the selection is changed
    if (currentSelection != currentPreview || currentSelectionPeriod != currentPreviewPeriod)
    {
        this.updatePreviewForSelection(currentSelection, currentSelectionPeriod);

        if (currentSelection != currentPreview)
        {
            element.updateSupplyProfileGraph();
        }

        currentPreview          = currentSelection;
        currentPreviewPeriod    = currentSelectionPeriod;
    }
}

private void updatePreviewForSelection(int _currentSelection = reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(supplyDemandOverviewTable.row()), 
                                        int _currentSelectionPeriod = supplyDemandOverviewTable.column())
{

    element.hideCurrentPreview();

    groupNoSummary.visible(true);

    element.updateButtons();

    isPreviewUpdated = false;

    if (_currentSelection >= #SupplyTotal && _currentSelection <= #SupplyWithdrawalKanbanLine)
    {
        supplyProfileChartPreviewTab.visible(true);
    }

    switch (_currentSelection)
    {
        case #DemandForecast:
            element.inventForecastPreview(_currentSelectionPeriod, _currentSelection, ForecastTableType::Demand);
            break;

        case #SupplyPlannedProductionOrdersForecast,
            #SupplyPmfPlannedBatchOrdersForecast,
            #SupplyPlannedKanbanForecast,
            #SupplyPlannedPurchaseOrdersForecast,
            #SupplyTransferPlannedOrdersForecast:
            element.inventForecastPreview(_currentSelectionPeriod, _currentSelection, ForecastTableType::Supply);
            break;

        case #DemandSalesOrders,
            #SupplyReturnSalesOrders:
            element.salesOrdersPreview(_currentSelectionPeriod, _currentSelection);
            break;

        case #SupplyPurchaseOrders,
            #DemandReturnPurchaseOrders:
            element.purchaseOrdersPreview(_currentSelectionPeriod, _currentSelection);
            break;

        case #SupplyProductionOrders:
            element.supplyProductionOrdersPreview(_currentSelectionPeriod);
            break;

        case #DemandPlannedTransferOrders,
            #SupplyPlannedProductionOrdersTotal,
            #SupplyPmfPlannedBatchOrdersTotal,
            #SupplyPlannedPurchaseOrdersTotal,
            #SupplyTransferPlannedOrdersTotal,
            #SupplyPlannedKanbanTotal,
            #SupplyTransferPlannedKanbansTotal:
            element.supplyPlannedOrdersPreview(_currentSelectionPeriod, _currentSelection);
            break;

        case #DemandTransferOrders,
            #SupplyTransferOrders:
            element.supplyDemandTransferOrdersPreview(_currentSelectionPeriod, _currentSelection);
            break;

        case #SupplyProductionKanban:
            element.supplyKanbanProductionPreview(_currentSelectionPeriod);
            break;

        case #SupplyWithdrawalKanbans,
            #DemandKanbanTransfer:
            element.supplyKanbanTransferPreview(_currentSelectionPeriod);
            break;
    }

}

public void updateTablePeriods()
{
    #define.periodDescription(3)
    int         periodCounter;

    reqSupplyDemandScheduleModel.parmTodayDate(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
    reqSupplyDemandScheduleModel.parmPeriodTemplate(periodTemplate);

    reqSupplyDemandScheduleModel.calcPeriods();

    periodTemplatePeriods = reqSupplyDemandScheduleModel.calculatedPeriods();

    // Remove existing period columns
    supplyDemandOverviewTable.deleteCols(0, supplyDemandOverviewTable.columns());

    // Create columns corresponding to the number of periods
    supplyDemandOverviewTable.insertCols(1, conLen(periodTemplatePeriods));

    boolean isReqSupplyDemandScheduleColumnLabelsFlightEnabled = ReqSupplyDemandScheduleColumnLabelsFlight::instance().isEnabled();

    // Traverse the periods
    for (periodCounter = 1; periodCounter <= conLen(periodTemplatePeriods); periodCounter++)
    {
        // Update column labels with period description
        if (isReqSupplyDemandScheduleColumnLabelsFlightEnabled)
        {
            supplyDemandOverviewTable.setColLabel(periodCounter, conPeek(conPeek(periodTemplatePeriods, periodCounter), #periodDescription));
        }
        else
        {
            supplyDemandOverviewTable.cell(periodCounter, 0).data(conPeek(conPeek(periodTemplatePeriods, periodCounter), #periodDescription));
        }
    }
}

public void updateTableRowLabels()
{
    renderRows = reqSupplyDemandScheduleControl.buildTableControl();
}

public void updateTitleArea()
{
    const str separator = ', ';

    if (element.parmItemId())
    {
        str inventDimText = element.parmInventDim().prefix(InventDim::dimFieldsList(), separator, ' ');

        FormTitle.text(inventDimText != ''? element.parmItemId() + separator + inventDimText:element.parmItemId());
        FormTitle.visible(true);

        if (itemDetailsPart)
        {
            itemDetailsPart.updateRanges(element.parmItemId());
        }
    }
    else
    if (element.parmForecastItemAllocationId())
    {
        FormTitle.visible(true);
        FormTitle.text(ForecastItemAllocation::find(element.parmForecastItemAllocationId()).Name);

        if (itemDetailsPart)
        {
            itemDetailsPart.updateRanges('');
        }
    }
}

public int task(int _taskId)
{
    #task
    int ret;

    ret = super(_taskId);

    if (ret && _taskId == #taskRefresh)
    {
        this.eventRefresh();
    }

    return ret;
}

public void salesOrdersPreview(int _currentSelectionPeriod, int _currentSelection)
{
    const int SalesOrderLinesRequestedPosition = 1;
    const int SalesOrderTotalQuantityPosition = 2;
    const int SalesOrderConfirmedLinesPosition = 3;
    const int SalesOrderConfirmedQuantityPosition = 4;
    const int SalesOrderOnTimeDeliveryPosition = 5;
    const int SalesOrderTotalAmountPosition = 6;

    // Activate preview tab
    salesPreviewTab.visible(true);
    // Sales orders
    salesPreviewTab.caption(strFmt("%1 - %2", "@SYS4363",  supplyDemandOverviewTable.colLabel(supplyDemandOverviewTable.column())));

    // Set filter for item allocaion key
    if (element.parmForecastItemAllocationId())
    {
        datasourceSalesLineAllocationKey.enabled(true);
        rangeSalesLineAllocationKey.value(element.parmForecastItemAllocationId());
        rangeSalesLineItemId.enabled(false);
    }
    // Set filter for item
    else
    {
        datasourceSalesLineAllocationKey.enabled(false);
        rangeSalesLineAllocationKey.value('');
        rangeSalesLineItemId.enabled(true);
        rangeSalesLineItemId.value(queryValue(element.parmItemId()));
    }
    // Set filter for inventory dimensions
    element.setInventDimRanges(salesLineInventDimQueryRanges);

    int dayOffset;
    TransDate fromDate  = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodFromDate);
    TransDate toDate    = conPeek(conPeek(reqSupplyDemandScheduleModel.calculatedPeriods(), _currentSelectionPeriod), #periodToDate);

    if (fromDate == dateNull())
    {
        dayOffset = 1;
    }

    str rangeStr = this.buildSalesLineQueryRange(_currentSelection);

    rangeSalesLineDate.value(rangeStr);

    rangeReqTrans.value(strFmt('( ((%1.%2 >= %3) && (%1.%2 <= %4)) )',
         rangeReqTrans.dataSource().name(),
         fieldStr(ReqTrans, ReqDate),
         date2StrXpp(fromdate + dayOffset),
        date2StrXpp(toDate)
        ));

    if (salesPreviewTab.isExpanded())
    {
        salesLine_ds.executeQuery();
        isPreviewUpdated = true;
    }

    summaryData = reqSupplyDemandScheduleModel.salesOrdersPreview(fromDate, toDate);

    groupNoSummary.visible(false);
    groupSalesOrdersSummary.visible(true);

    // Update the form control with the summary data
    salesOrderLinesRequested.realValue(conPeek(summaryData, SalesOrderLinesRequestedPosition));
    salesOrderTotalQuantity.realValue(conPeek(summaryData, SalesOrderTotalQuantityPosition));
    salesOrderConfirmedLines.realValue(conPeek(summaryData, SalesOrderConfirmedLinesPosition));
    salesOrderConfirmedQuantity.realValue(conPeek(summaryData, SalesOrderConfirmedQuantityPosition));
    salesOrderOnTimeDelivery.realValue(conPeek(summaryData, SalesOrderOnTimeDeliveryPosition));
    salesOrderTotalAmount.realValue(conPeek(summaryData, SalesOrderTotalAmountPosition));

    summaryTab.visible(true);
}

private str buildSalesLineQueryRange(int _currentSelection)
{
    str rangeStr = '(';

    if (_currentSelection == #SupplyReturnSalesOrders)
    {
        rangeStr += strFmt('(%1.%2 < 0)', rangeSalesLineDate.dataSource().name(), fieldStr(SalesLine, SalesQty));
    }
    else if (_currentSelection == #DemandSalesOrders)
    {
        rangeStr += strFmt('(%1.%2 >= 0)', rangeSalesLineDate.dataSource().name(), fieldStr(SalesLine, SalesQty));
    }
    rangeStr +=')';

    return rangeStr;
}

private boolean checkParmsAndAddItemFilter(Query _query, int _table, str _field)
{
    if (!element.isItemIdOrAllocationIdSet())
    {
            return false;
    }

    if (element.parmItemId())
    {
        _query.addQueryFilter(_query.dataSourceTable(_table), _field).value(queryValue(element.parmItemId()));
    }

    return true;
}

public void init()
{
    QueryBuildDataSource        qbdsPurchLine;
    Query                       query;

    super();

    rangePurchLineItemId    = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine, ItemId));
    rangePurchLineDate      = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine, ConfirmedDlv));
    rangePurchLineStatus    = this.query().dataSourceTable(tableNum(PurchLine)).addRange(fieldNum(PurchLine, PurchStatus));

    rangePurchLineStatus.value(queryValue(PurchStatus::Backorder));

    query = this.query();

    qbdsPurchLine = query.dataSourceTable(tableNum(PurchLine));
    qbdsPurchLine.clearDynalinks();

    datasourcePurchLineAllocationKey = qbdsPurchLine.addDataSource(tableNum(ForecastItemAllocationLine));
    datasourcePurchLineAllocationKey.joinMode(JoinMode::ExistsJoin);
    datasourcePurchLineAllocationKey.addLink(fieldNum(PurchLine, ItemId), fieldNum(ForecastItemAllocationLine, ItemId));
    datasourcePurchLineAllocationKey.enabled(false);

    rangePurchLineAllocationKey = datasourcePurchLineAllocationKey.addRange(fieldNum(ForecastItemAllocationLine, AllocationId));
}

public void executeQuery()
{
    if (element.checkParmsAndAddItemFilter(this.query(), tableNum(PurchLine), fieldStr(PurchLine, ItemId)))
    {
        super();
    }
}

public void init()
{
    QueryBuildDataSource        qbdsProdTable;
    Query                       query;

    super();

    // There is an automatic dynalink to ProdId of parent form's ReqTrans record
    this.query().dataSourceTable(this.table()).clearDynalinks();

    rangeProdTableItemId = this.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, ItemId));
    rangeProdTableDeliveryDate = this.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, SchedEnd));
    rangeProdTableSchedEndDate = this.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, SchedEnd));
    rangeProdTableStatus = this.query().dataSourceTable(tableNum(ProdTable)).addRange(fieldNum(ProdTable, ProdStatus));

    rangeProdTableStatus.value(SysQuery::valueNot(ProdStatus::Completed));

    query = this.query();

    qbdsProdTable = query.dataSourceTable(tableNum(ProdTable));

    datasourceProdTableAllocationKey = qbdsProdTable.addDataSource(tableNum(ForecastItemAllocationLine));
    datasourceProdTableAllocationKey.joinMode(JoinMode::ExistsJoin);
    datasourceProdTableAllocationKey.addLink(fieldNum(ProdTable, ItemId), fieldNum(ForecastItemAllocationLine, ItemId));
    datasourceProdTableAllocationKey.enabled(false);

    rangeProdTableAllocationKey = datasourceProdTableAllocationKey.addRange(fieldNum(ForecastItemAllocationLine, AllocationId));
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void executeQuery()
{
    if (element.checkParmsAndAddItemFilter(this.query(), tableNum(ProdTable), fieldStr(ProdTable, ItemId)))
    {
        super();
    }
}

public void init()
{
    Query                       query;

    super();

    query = this.query();

    qbdsForecastInvent = query.dataSourceTable(tableNum(ForecastInvent));

    qbdsForecastModel = qbdsForecastInvent.addDataSource(tableNum(ForecastModel));
    qbdsForecastModel.joinMode(JoinMode::ExistsJoin);
    qbdsForecastModel.addLink(fieldNum(ForecastInvent, ModelId),fieldNum(ForecastModel, SubModelId));

    qbdsForecastAllocationLine = qbdsForecastInvent.addDataSource(tableNum(ForecastItemAllocationLine));
    qbdsForecastAllocationLine.joinMode(JoinMode::ExistsJoin);
    qbdsForecastAllocationLine.addLink(fieldNum(ForecastInvent, ItemId), fieldNum(ForecastItemAllocationLine, ItemId));

    qbdsForecastReqTransDim = qbdsForecastInvent.addDataSource(tableNum(InventDim));
    qbdsForecastReqTransDim.joinMode(JoinMode::ExistsJoin);
    qbdsForecastReqTransDim.addLink(fieldNum(ForecastInvent, InventDimId), fieldNum(InventDim, InventDimId));
}

public void executeQuery()
{
    if (element.checkParmsAndAddItemFilter(this.query(), tableNum(ForecastInvent), fieldStr(ForecastInvent, ItemId)))
    {
        super();
    }
}

public void init()
{
    QueryBuildDataSource        qbdsSalesLine;
    QueryBuildDataSource        qbdsReqTrans;
    QueryBuildDataSource        qbdsInventTransOriginSalesLine;
    QueryBuildRange             rangeSalesStatus;
    Query                       query;

    super();

    rangeSalesLineItemId    = this.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, ItemId));
    rangeSalesLineDate      = this.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, ReceiptDateRequested));
    rangeSalesStatus        = this.query().dataSourceTable(tableNum(SalesLine)).addRange(fieldNum(SalesLine, SalesStatus));

    qbdsReqTrans = this.query().dataSourceTable(tableNum(SalesLine)).addDataSource(tableNum(ReqTrans));
    qbdsReqTrans.addLink(fieldNum(SalesLine, SalesId), fieldNum(ReqTrans, RefId));
    qbdsReqTrans.addRange(fieldNum(ReqTrans, RefType)).value(queryValue(ReqRefType::Sales));
    if (element.parmReqPlanVersionRecId())
    {
        qbdsReqTrans.addRange(fieldNum(ReqTrans, PlanVersion)).value(queryValue(element.parmReqPlanVersionRecId()));
    }
    qbdsReqTrans.joinMode(JoinMode::ExistsJoin);
    qbdsReqTrans.fetchMode(QueryFetchMode::One2One);
    rangeReqTrans = qbdsReqTrans.addRange(fieldNum(ReqTrans, ReqDate));

    qbdsInventTransOriginSalesLine = qbdsReqTrans.addDataSource(tableNum(InventTransOriginSalesLine));
    qbdsInventTransOriginSalesLine.fetchMode(QueryFetchMode::One2One);
    qbdsInventTransOriginSalesLine.joinMode(JoinMode::ExistsJoin);
    qbdsInventTransOriginSalesLine.addLink(fieldNum(ReqTrans, InventTransOrigin), fieldNum(InventTransOriginSalesLine, InventTransOrigin));
    qbdsInventTransOriginSalesLine.addLink(fieldNum(Salesline, InventTransId), fieldNum(InventTransOriginSalesLine, SalesLineInventTransId), this.query().dataSourceTable(tableNum(SalesLine)).name());
    qbdsInventTransOriginSalesLine.addLink(fieldNum(Salesline, DataAreaId), fieldNum(InventTransOriginSalesLine, SaleslineDataAreaId), this.query().dataSourceTable(tableNum(SalesLine)).name());

    query = this.query();

    qbdsSalesLine = query.dataSourceTable(tableNum(SalesLine));

    datasourceSalesLineAllocationKey = qbdsSalesLine.addDataSource(tableNum(ForecastItemAllocationLine));
    datasourceSalesLineAllocationKey.joinMode(JoinMode::ExistsJoin);
    datasourceSalesLineAllocationKey.addLink(fieldNum(SalesLine, ItemId), fieldNum(ForecastItemAllocationLine, ItemId));
    datasourceSalesLineAllocationKey.enabled(false);

    rangeSalesLineAllocationKey = datasourceSalesLineAllocationKey.addRange(fieldNum(ForecastItemAllocationLine, AllocationId));

    rangeSalesStatus.value(queryValue(SalesStatus::Backorder));
}

