private void addTransferDemandAndInventDimDataSources()
{
    int qbdsCount = reqPO_DS.query().dataSourceCount();
    QueryBuildDataSource lastQbds = reqPO_DS.query().dataSourceNo(qbdsCount);
    datasourceTransferDemand = ReqSupplyDemandScheduleModel::addTransferDemandReqTransDatasource(lastQbds, reqPO_DS.query().dataSourceTable(tableNum(ReqPO)));
    datasourceTransferDemand.addLink(fieldNum(ReqPO, PlanVersion), fieldNum(ReqTrans, PlanVersion), reqPO_DS.query().dataSourceTable(tableNum(ReqPO)).name());
    QueryBuildDataSource qbdsInventDimDemand = datasourceTransferDemand.addDataSource(tableNum(InventDim));
    qbdsInventDimDemand.joinMode(JoinMode::ExistsJoin);
    qbdsInventDimDemand.addLink(fieldNum(ReqTrans, CovInventDimId), fieldNum(InventDim, InventDimId), datasourceTransferDemand.name());

    reqPOInventDimPlannedTransferSiteWarehouseQueryRanges.insert(fieldNum(InventDim, InventSiteId), qbdsInventDimDemand.addRange(fieldNum(InventDim, InventSiteId)));
    reqPOInventDimPlannedTransferSiteWarehouseQueryRanges.insert(fieldNum(InventDim, InventLocationId), qbdsInventDimDemand.addRange(fieldNum(InventDim, InventLocationId)));
}

