    inventTransferInventDimToQueryRanges.insert(fieldNum(InventDim, InventLocationId), qbdsInventTransferTable.addRange(fieldNum(InventTransferTable, InventLocationIdTo)));

    rangeInventTransferLineStatus = this.query().dataSourceTable(tableNum(InventTransferTable)).addRange(fieldNum(InventTransferTable, TransferStatus));
    rangeInventTransferLineStatus.value(queryValue(InventTransferStatus::Created));

    QueryBuildDataSource qbdsInventLocationFrom = qbdsInventTransferTable.addDataSource(tableNum(InventLocation));
    qbdsInventLocationFrom.joinMode(JoinMode::InnerJoin);

    QueryBuildDataSource qbdsInventLocationTo = qbdsInventTransferTable.addDataSource(tableNum(InventLocation));
    qbdsInventLocationTo.joinMode(JoinMode::InnerJoin);

    qbdsInventLocationFrom.addLink(fieldNum(InventTransferTable, InventLocationIdFrom), fieldNum(InventLocation, InventLocationId));
    qbdsInventLocationTo.addLink(fieldNum(InventTransferTable, InventLocationIdTo), fieldNum(InventLocation, InventLocationId));

    inventTransferInventDimFromQueryRanges.insert(fieldNum(InventDim, InventSiteId), qbdsInventLocationFrom.addRange(fieldNum(InventLocation, InventSiteId)));
    inventTransferInventDimToQueryRanges.insert(fieldNum(InventDim, InventSiteId), qbdsInventLocationTo.addRange(fieldNum(InventLocation, InventSiteId)));
}

