public void init()
{
    super();

    Query query = this.query();
    QueryBuildDataSource qbds = query.dataSourceName(tableStr(ReqTrans));

    // manually clear the links and add the correct relation to the ReqPO table
    // as there are two active PK-FK pairs between those two tables
    qbds.clearLinks();
    qbds.clearRanges();
    qbds.clearDynalinks();

    qbds.addLink(fieldNum(ReqPo, RefId), fieldNum(ReqTrans, RefId));
    qbds.addLink(fieldNum(ReqPo, RefType), fieldNum(ReqTrans, RefType));
    qbds.addLink(fieldNum(ReqPo, PlanVersion), fieldNum(ReqTrans, PlanVersion));

    rangeReqPOSubClassification = qbds.addRange(fieldNum(ReqTrans, SupplyDemandSubClassification));
    rangeReqPODirection = qbds.addRange(fieldNum(ReqTrans, Direction));
    rangeReqPODirection.value(queryValue(InventDirection::Receipt));
    rangeReqPOReqTransPlanVersion = qbds.addRange(fieldNum(ReqTrans, PlanVersion));

    reqTransForm.reqTransDSInitPost(reqTrans);
}

