void clicked()
{
    if (this.toggleValue())
    {
        element.recalculateDelays(true);

        delayWarningMessage.visible(true);
        infoMessageArea.visible(true);
    }
    else
    {
        delays = connull();

        delayWarningMessage.visible(false);
        infoMessageArea.visible(false);
    }
    element.eventRefresh();
}

