void clicked()
{
    KanbanRule          kanbanRule;
    Args                args;
    MenuFunction        menuFunction;

    if (element.checkActiveMasterPlanVersion())
    {
        args = new Args();

        if (itemId)
        {
            kanbanRule = element.findKanbanRuleForItem();

            if (kanbanRule)
            {
                args.record(kanbanRule);
                args.caller(element);

                menuFunction = new MenuFunction(menuitemActionStr(KanbansCreate), MenuItemType::Action);

                menuFunction.run(args);
            }
            else
            {
                // No active kanban rule exists for the selected item %1, site %2 and warehouse %3
                error(strFmt("@SYS340807",
                             itemId,
                             element.parmInventDim().InventSiteId,
                             element.parmInventDim().InventLocationId));
            }
        }
    }
}

