public void activeCellChanged()
{
    container   currentRowData;
    boolean     isExpanded;

    super();

    if (currentRow && supplyDemandOverviewTable.row() != currentRow)
    {
        currentRowData = reqSupplyDemandScheduleControl.currentRowData(supplyDemandOverviewTable.row());

        if (conPeek(currentRowData, #isExpandable))
        {
            isExpanded = reqSupplyDemandScheduleControl.isRowExpanded(supplyDemandOverviewTable.row());
            expand.visible(!isExpanded);
            expand.enabled(true);
            collapse.visible(isExpanded);
            collapse.enabled(true);
        }
        else
        {
            expand.enabled(false);
            collapse.enabled(false);
        }

        element.updatePreview();

        element.updateInfoParts();
    }
    else
    if (currentColumn && supplyDemandOverviewTable.column() != currentColumn)
    {
        element.updatePreview();

        element.updateInfoParts();
    }

    if (currentColumn && supplyDemandOverviewTable.column() != currentColumn)
    {
        element.delayMessage();
        element.inventoryLevelMessage();
    }

    currentRow = supplyDemandOverviewTable.row();
    currentColumn = supplyDemandOverviewTable.column();
}

FormControl editControl(int _column, int _row)
{
    Qty     periodEndInventory;
    Days    delay;
    int     rowType = reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(_row);

    if (rowType == #PeriodEndInventory)
    {
        periodEndInventory = conPeek(conPeek(tableData, reqSupplyDemandScheduleControl.getTableNumberFromRowNumber(_row)), _column);
        if (minimumInventoryLevel && periodEndInventory < minimumInventoryLevel)
        {
            return underMinimumInventoryLevel;
        }
        else
        if (maximumInventoryLevel && periodEndInventory > maximumInventoryLevel)
        {
            return overMaximumInventoryLevel;
        }
    }

    if (rowType == #SupplyTotal || rowType == #DemandTotal)
    {
        return headerControl;
    }

    if (rowType == #DemandSalesOrders)
    {
        if (conLen(delays) > 0)
        {
            delay = conPeek(conPeek(delays, 1), _column);
        }
    }
    else
    if (rowType == #SupplyPurchaseOrders)
    {
        if (conLen(delays) > 0)
        {
            delay = conPeek(conPeek(delays, 2), _column);
        }
    }
    else
    if (rowType == #SupplyProductionOrders)
    {
        if (conLen(delays) > 0)
        {
            delay = conPeek(conPeek(delays, 3), _column);
        }
    }

    if (delay > 0)
    {
        if (delay < 3)
        {
            return lowDelayControl;
        }
        else
        if (delay >= 3 && delay <= 8)
        {
            return mediumDelayControl;
        }
        else
        {
            return highDelayControl;
        }
    }

    return StringEdit;
}

