        isPreviewUpdated = true;
    }

    super();
}

void clicked()
{
    Args        args = new Args();
    KanbanRule  kanbanRule = KanbanRule::find(kanbanTransfer.KanbanRule);

    args.record(kanbanRule);
    new MenuFunction(menuitemDisplayStr(KanbanRules), MenuItemType::Display).run(args);
}
