public void buildTraceTree(boolean _forceRefresh = false)
{
    ReqTraceLog         reqTraceLogPrev;
    ReqTraceLog         reqTraceLogLocal;
    ReqTraceLogDetail   reqTraceLogDetailLocal;
    int                 logNodeId;
    int                 itemNodeId;
    int                 processStatusNodeId;
    int                 nodeId;
    int                 rootNodeId;
    SysDictEnum         dictEnumMessage = new SysDictEnum(enumNum(ReqTraceMessageType));
    SysDictEnum         dictEnumDetail = new SysDictEnum(enumNum(ReqTraceMessageDetailType));

    if (treeHasBeenBuild && !_forceRefresh)
    {
        return;
    }

    traceTree.deleteAll();

    rootNodeId = traceTree.getRoot();

    while select reqTraceLogLocal
        order by ReqProcessStatus, bomLevel, ReqLevelState, ItemId, Sequence
        where reqTraceLogLocal.TraceId == ReqTraceLog::findTraceForReqTrans(reqTransCaller)
        outer join reqTraceLogDetailLocal
            where reqTraceLogDetailLocal.ReqTraceLog == reqTraceLogLocal.RecId
    {
        if (reqTraceLogPrev.ReqProcessStatus != reqTraceLogLocal.ReqProcessStatus || processStatusNodeId == 0)
        {
            // Add status grouping node
            nodeId = traceTree.add(rootNodeId, nodeId, enum2str(reqTraceLogLocal.ReqProcessStatus));
            processStatusNodeId = nodeId;
            itemNodeId = 0;
        }

        if (reqTraceLogPrev.ItemId != reqTraceLogLocal.ItemId || itemNodeId == 0)
        {
            // Add item grouping node
            nodeId = traceTree.add(processStatusNodeId, nodeId, InventTable::name(reqTraceLogLocal.ItemId));
            itemNodeId = nodeId;
            logNodeId = 0;
        }

        if (reqTraceLogPrev.RecId != reqTraceLogLocal.RecId || logNodeId == 0)
        {
            // Add main log entry node
            nodeId = traceTree.add(itemNodeId, nodeId, ReqTraceLogMessage::getText(dictEnumMessage.value2Label(enum2int(reqTraceLogLocal.MessageType)), reqTraceLogLocal.MessageCon));
            logNodeId = nodeId;
        }

        if (reqTraceLogDetailLocal.RecId)
        {
            // Add detail entry
            nodeId = traceTree.add(logNodeId, nodeId, ReqTraceLogMessage::getText(dictEnumDetail.value2Label(reqTraceLogDetailLocal.MessageDetailType), reqTraceLogDetailLocal.MessageCon));
        }

        reqTraceLogPrev.data(reqTraceLogLocal);
    }

    treeHasBeenBuild = true;
}

void close()
