}

void enableButtonsCaller()
{
    SecurityRights  securityRights;
    boolean         doEnable = false;

    if (   element.args().caller()
        && (   element.args().dataset() == tableNum(SalesLine)
            || element.args().dataset() == tableNum(ProdTable)
            || element.args().dataset() == tableNum(PurchReqLine))
        && reqTransCaller.RecId
        && reqTransCaller.FuturesDate > reqTransCaller.ReqDateDlvOrig)
    {
        securityRights = SecurityRights::newUser(curUserId());
        if (securityRights.tableAccessRight(tableId2name(element.args().dataset())) >= AccessRight::Edit)
        {
            doEnable = true;
        }
    }

    if (buttonTransferFuturesDate.enabled() != doEnable)
    {
        buttonTransferFuturesDate.enabled(doEnable);
    }

    buttonReqCalcExplode.enabled(reqTransForm.mustEnableReqCalcExplodeButton(reqTransCaller));

    buttonRedraw.enabled(reqTransCaller.RecId);
}

