void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    setTabInitOk = new Set(Types::Enum);
    reqTransForm = ReqTransFormExplosion::newFormRun(element);

    super();

    reqTransForm.parmCtrlTree(tree);
    reqTransForm.parmCtrlReqExplodeType(reqExplodeType);
    reqTransForm.parmCtrlFormatExplain(formatExplain);
    reqTransForm.parmReqTransCaption(reqTransCaption);

    traceTreeControlMark = SysFormTreeControlMark::newTreeControl(traceTree);

    reqTransForm.formInitPost();
}

void initTab(boolean _init)
{
    if (_init)
    {
        setTabInitOk    = new Set(Types::Enum);
    }

    if (tabPageOverview.isActivePage() || tabPageAction.isActivePage())
    {
        element.initTabOverview();
    }

    if (tapPageGantt.isActivePage())
    {
        element.initTabGantt();
    }

    if (tabPageOnhand.isActivePage())
    {
        element.initTabOnHand();
    }
}

void initTabGantt()
{
    if (reqTransForm.tmpReqExplosionTree().ReqGanttShow == ReqGanttShow::CurrentTreeNode)
    {
        reqTransForm.ganttBuild(GanttControl1);
    }
    else
    {
        if (! setTabInitOk.in(#tabGantt))
        {
            reqTransForm.ganttBuild(GanttControl1);
            setTabInitOk.add(#tabGantt);
        }
    }

    if (!ganttControlInitialized)
    {
        ganttControlInitialized = true;

        GanttControlVisualization_ReqExplosion visualization = element.ganttClient().parmGanttVisualization();
        GanttControl1.onActivityDblClick += eventhandler(visualization.onActivityDblClick);
        GanttControl1.onActivitySelected += eventhandler(visualization.onActivtySelected);
    }
    else
    {
        GanttControl1.Refresh();
    }

    element.enableButtonsCaller();
}

void initTabOnHand()
{
    if (! setTabInitOk.in(#tabOnhand))
    {
        reqTransForm.tmpReqExplosionOnhandBuild();

        setTabInitOk.add(#tabOnhand);
    }
    element.enableButtonsCaller();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void initTabOverview()
{
    if (! setTabInitOk.in(#tabOverview))
    {
        reqTransForm.treeBuild();
        reqTransForm.tmpReqTransBuild();

        setTabInitOk.add(#tabOverview);
        element.updateExplosionViewButton();
    }
    element.enableButtonsCaller();
}

