    return tabPageOnhand.isActivePage();
}

public void redrawTab()
{
    switch (tab.tab())
    {
        case #tabOverview :
            reqTransForm.treeRedraw();

            break;
    }

    if (setTabInitOk.in(#tabGantt))
    {
        setTabInitOk.remove(#tabGantt);
    }
}

