public int active()
{
    int ret;

    ret = super();

    treeHasBeenBuild = false;
    if (tab.tab() == #tabExplanation)
    {
        element.buildTraceTree();
    }

    reqTransCaption.text(reqTransCaller.tooltipRecord());
    reqTransForm.build();

    tmpReqTrans_ds.active();

    return ret;
}

public void executeQuery()
{
    ReqTrans    reqTransFind;
    ItemId      itemId = ReqCalc::argsItemId(element.args());

    if (itemId)
    {
        ReqTransUpdate::runItemId(itemId);
    }

    grandFatherLinkId = element.args().record().RecId;

    reqTransFind = ReqTrans::findCommon(
        ReqPlan::findReqPlanId(reqTransForm.parmReqPlanId()).findActiveVersion().RecId,
        element.args().record());

    rangeReqPlanVersion.value(queryValue(reqTransFind.PlanVersion));
    rangeRefType.value(queryValue(reqTransFind.RefType));
    rangeRefId.value(queryValue(reqTransFind.RefId));
    rangeInventTransOrigin.value(queryValue(reqTransFind.InventTransOrigin));
    rangeItemId.value(queryValue(reqTransFind.ItemId));
    rangeRecId.value(SysQuery::valueUnlimited());

    super();

    reqTransForm.reqTransCallerDSExecuteQueryPost(reqTransCaller, element.args().record());
}

display ImageRes futuresPostpone(ReqTrans _reqTrans)
{
    if (! reqTransForm)
    {
        return -1;
    }

    if (! _reqTrans.FuturesMarked)
    {
        return reqTransForm.displayImageFuturesOK(_reqTrans);
    }

    return reqTransForm.displayImageFuturesPost(_reqTrans);
}

public void init()
{
    Query                   query = new Query();
    QueryBuildDataSource    qbs;

    super();

    reqTransForm.reqTransCallerDSInitPost(reqTransCaller);

    qbs = query.addDataSource(tableNum(ReqTrans));
    qbs.name(reqTransCaller_ds.name());

    rangeReqPlanVersion     = qbs.addRange(fieldNum(ReqTrans,PlanVersion));
    rangeRefType            = qbs.addRange(fieldNum(ReqTrans,RefType));
    rangeRefId              = qbs.addRange(fieldNum(ReqTrans,RefId));
    rangeInventTransOrigin  = qbs.addRange(fieldNum(ReqTrans,InventTransOrigin));
    rangeItemId             = qbs.addRange(fieldNum(ReqTrans,ItemId));

    rangeRecId              = qbs.addRange(fieldNum(ReqTrans,RecId));

    rangeReqPlanVersion.status(RangeStatus::Hidden);
    rangeRecId.status(RangeStatus::Hidden);

    this.query(query);
}

public void jumpRef()
{
    if (reqTransCaller.isPlannedOrder() || reqTransCaller.isDerivedFromPlannedOrder())
    {
        super();
    }
    else
    {
        reqTransCaller.jumpRefRefId(element);
    }
}

