public void init()
{
    Query                query;
    QueryBuildDataSource qbs;

    super();

    tmpReqTrans.setTmp();
    tmpReqTrans.checkRecord(false);

    reqTransForm.reqTransDSInitPost(tmpReqTrans);

    query = new Query();
    qbs   = query.addDataSource(tableNum(ReqTrans));
    qbs.name(tmpReqTrans_ds.name());

    qbs.addSortField(fieldNum(ReqTrans,Level));
    qbs.addSortField(fieldNum(ReqTrans,RefId));

    this.query(query);
}

