void clicked()
{
    SalesLine           salesLine;
    ProdTable           prodTable;
    PurchReqLine        purchReqLine;

    super();

    switch (element.args().dataset())
    {
        case tableNum(SalesLine):
            salesLine = element.args().record();
            salesLine.ShippingDateConfirmed = reqTransCaller.FuturesDate;
            FormDataUtil::getFormDataSource(salesLine).write();

            break;

        case tableNum(ProdTable):
            prodTable = element.args().record();
            prodTable.DlvDate = reqTransCaller.FuturesDate;
            FormDataUtil::getFormDataSource(prodTable).write();

            break;

        case tableNum(PurchReqLine):
            purchReqLine = element.args().record();
            purchReqLine.RequiredDate = reqTransCaller.FuturesDate;
            FormDataUtil::getFormDataSource(purchReqLine).write();

            break;
    }
}

