void clicked()
{
    boolean showCapacity;
    boolean showDerived;
    boolean isRefTypeBomPlannedOrder;
    boolean isRefTypeProduction;
    boolean isRefTypePmfPlannedProdBatch;

    showCapacity = ReqTrans::refTypeCanReserveWorkCenter(tmpReqTrans.RefType);
    showDerived  = ReqTrans::derivedRefType(tmpReqTrans.RefType) != ReqRefType::None;
    isRefTypeBomPlannedOrder = tmpReqTrans.RefType == ReqRefType::BOMPlannedOrder;
    isRefTypePmfPlannedProdBatch = ReqTransExplosionRouteAndJobsVisibilityFlight::instance().isEnabled() && tmpReqTrans.RefType == ReqRefType::PmfPlannedProdBatch;
    isRefTypeProduction =  tmpReqTrans.RefType == ReqRefType::Production;

    buttonReqTransDerived.visible(showDerived);
    separatorReqTransDerived.visible(showDerived);

    buttonReqRoute.visible(     isRefTypeBomPlannedOrder || isRefTypePmfPlannedProdBatch);
    buttonReqRouteJobs.visible( isRefTypeBomPlannedOrder || isRefTypePmfPlannedProdBatch);

    buttonProdBOM.visible(      isRefTypeProduction);
    buttonProdRoute.visible(   isRefTypeProduction);
    buttonProdRouteJob.visible( isRefTypeProduction);

    separatorBOMRoute.visible(showCapacity);
    separatorWrkCtrCapRes.visible(showCapacity);
    buttonWrkCtrCapRes.visible(showCapacity);

    super();
}

