/// <summary>
///     Intitializes the <c>TmpReqInventDim</c> data source if it is not initialized.
/// </summary>
private void ensureTmpReqInventDimInitialized()
{
    if (!tmpReqInventDim)
    {
        reqTransForm.doResearch();
    }
}

public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    reqTransForm = ReqTransFormOverview::newFormRun(element);

    super();

    reqTransForm.parmCtrlGridReqDate(reqTrans_ReqDate);
    reqTransForm.parmCtrlTabTrans(tabReqTrans);
    reqTransForm.parmCtrlTabSum(ctrlTabSum);

    reqTransForm.parmTmpReqInventDim(tmpReqInventDim);

    reqTransForm.parmCtrlGroupSettlement(groupSettlement);

    reqTransForm.parmCtrlTabPagePeriod(tabPeriod);
    reqTransForm.parmReqTmpPeriodQty(reqTmpPeriodQty);
    reqTransForm.parmCtrlGraphPeriod(graphPeriod);
    reqTransForm.parmCtrlGridPeriod(gridPeriod);

    reqTransForm.formInitPost();

    reqTransForm.parmPlanFilterControl(PlanFilter);
    reqTransForm.parmPeriodChartTypeMenuButtonGroup(periodModeGraphType);
    reqTransForm.parmPeriodTemplateMenuButtonGroup(periodTemplate);
    reqTransForm.initSelectionFilters();

    this.onModifiedPlanFilter();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

/// <summary>
/// A hook method that will be called when the PlanFilter changed.
/// </summary>
[Wrappable(true)]
internal void onModifiedPlanFilter()
{
}

void initDesign()
{
    boolean isMasterPlan = reqTransForm.isMasterPlan();

    sumOnhand.visible(isMasterPlan);
    sumReceipt.visible(isMasterPlan);
    sumSalesQty.visible(isMasterPlan);
    sumProdIssueQty.visible(isMasterPlan);

    if (ReqTransOverviewRequestedDateSortingFeature::instance().isEnabled())
    {
        ButtonGroupReqSorting.visible(true);
        reqTrans_ReqDate.dataField(0);
        reqTrans_ReqDate.dataMethod('transdate');
    }

    element.initButtonText();
}

public void run()
{
    super();

    this.ensureTmpReqInventDimInitialized();

    if (element.args().parm() == 'setupgen')
    {
        tabSumSetup.setFocus();
        tabTransGeneral.setFocus();
    }
}

public void setSortingButtonEnablement()
{
    if (ButtonGroupReqSorting.visible())
    {
        ReqSortingReqDate.enabled(reqTransForm.parmReqSorting() != ReqSorting::ReqDate);
        ReqSortingReqDateDlvOrig.enabled(reqTransForm.parmReqSorting() != ReqSorting::ReqDateDlvOrig);
    }
}

public int task(int _taskId)
{
    #Task

    int ret;

    if (_taskId == #taskF5
       && reqTransForm.reqPlanData().parmReqPlanId() == ReqPlanSched::defaultDynamicId())
    {
        ReqTransUpdate::runItemId(reqTransForm.parmItemId());
    }

    ret = super(_taskId);

    return ret;
}

display TimeFenceCoverage timeFenceCoverage(TmpReqInventDim _tmp)
{
    ReqPlanData _reqPlanData = reqTransForm.reqPlanData();
    return _reqPlanData.timeFenceCoverage(reqTransForm.findReqSetupDim(_tmp));
}

display ReqMaxNegativeDays maxNegativeDays(TmpReqInventDim _tmp)
{
    ReqPlanData _reqPlanData = reqTransForm.reqPlanData();
    return reqTransForm.findReqSetupDim(_tmp).maxNegativeDays(dateNull(), dateNull(), _reqPlanData);
}

public void linkActive()
{
    reqTransForm.initFromCaller();
    reqTransForm.updatePlanVersionSelection();
    reqTransForm.initReqTransCalc(false);

    reqTransForm.initTmpReq();

    super();

    if (!InventTable::isItemActiveForPlanning(reqTransForm.reqSetup().itemId()))
    {
        InventTable notPlannedItem = InventTable::find(reqTransForm.reqSetup().itemId());
        warning(strFmt("@SCM:ItemNotPlannedByMasterPlanning", notPlannedItem.ItemId, notPlannedItem.ProductLifecycleStateId));
    }

    tmpReqInventDim_ds.findCaller();

    reqTransForm.initTabTrans(true);

    element.updateDesign(InventDimFormDesignUpdate::Active);
}

void findCaller()
{
    TmpReqInventDim        tmpReqInventDimLocal;

    if (element.args().dataset())
    {
        reqTransCaller = ReqTrans::findCommon(
            ReqPlan::findReqPlanId(reqTransForm.parmReqPlanId()).findActiveVersion().RecId,
            element.args().record());

        if (reqTransCaller.RecId)

        {
            tmpReqInventDimLocal = TmpReqInventDim::findInventDimId(reqTransCaller.CovInventDimId,tmpReqInventDim);
            tmpReqInventDim_ds.findRecord(tmpReqInventDimLocal);

            if (element.args().lookupRecord() && element.args().lookupRecord().TableId != tableNum(TmpReqInventDim))
            {
                element.args().lookupRecord(tmpReqInventDimLocal);
            }
        }
    }
}

