display PdsCWReqQty cwQty(ReqTrans _reqTrans)
{
    return PdsCatchWeight::cwQty(_reqTrans.ItemId, reqTransForm.displayTransQty(_reqTrans));
}

public void linkActive() 
{
    super();

    if (reqTransCaller &&
        reqTransCaller.CovInventDimId == tmpReqInventDim.CovInventDimId)
    {
        // The code is using findRecord() for the following reasons:
        // 1. New API positionToRecord() throws exceptions in current scenario.
        // 2. Focus will not be on the expected line if using lookupRecord().
        // 3. Sorting will not work if using lookupField() and lookupValue().
        // 4. This approach secures that both sorting and setting focus behave properly.
        reqTrans_ds.findRecord(reqTransCaller);

        reqTransCaller = null;
    }
}

