public void init()
{
    super();

    QueryBuildDataSource qbds = EcoResProductTranslations_DS.queryBuildDataSource();
    qbds.clearLinks();
    qbds.addLink(fieldnum(InventTable, Product), fieldnum(EcoResProductTranslations, Product));

    if (FeatureStateProvider::isFeatureEnabled(EcoResProductTranslationFallbackFeature::instance()) && EcoResProductParameters::find().UseFallbackTranslation)
    {
        qbds.addRange(fieldNum(EcoResProductTranslations, LanguageId)).value(EcoResProductParametersFallbackLanguage::getSelectedFallbackLanguage());
    }
}

void clicked()
{
    reqCalcForecastItem.enabled(reqTransForm.reqPlanType() == ReqPlanType::ForecastPlan ? true  : false);
    reqCalcScheduleItem.enabled(reqTransForm.reqPlanType() == ReqPlanType::ForecastPlan ? false : true);

    super();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    reqTransForm.planIdSelectionChanged(this);

    element.onModifiedPlanFilter();

    return ret;
}

void clicked()
{
    super();

    reqTransForm.parmShowPeriodGraph(reqTransForm.parmShowPeriodGraph() ? false : true);
    reqTransForm.activateTabPeriod();

    element.initButtonText();
}

void clicked()
{
    boolean showCapacity;
    boolean showDerived;
    boolean isRefTypeBomPlannedOrder;
    boolean isRefTypeProduction;

    showCapacity = ReqTrans::refTypeCanReserveWorkCenter(reqTrans.RefType);
    showDerived  = ReqTrans::derivedRefType(reqTrans.RefType) != ReqRefType::None;
    isRefTypeBomPlannedOrder = reqTrans.RefType == ReqRefType::BOMPlannedOrder;
    isRefTypeProduction =  reqTrans.RefType == ReqRefType::Production;

    buttonReqTransDerived.visible(showDerived);
    separatorReqTransDerived.visible(showDerived);

    buttonReqRoute.visible(isRefTypeBomPlannedOrder);
    buttonReqRouteJobs.visible(isRefTypeBomPlannedOrder);

    buttonProdBOM.visible(isRefTypeProduction);
    buttonProdRoute.visible(isRefTypeProduction);
    buttonProdRouteJob.visible(isRefTypeProduction);

    separatorBOMRoute.visible(showCapacity);
    separatorWrkCtrCapRes.visible(showCapacity);
    buttonWrkCtrCapRes.visible(showCapacity);

    super();
}

public void clicked()
