/// <summary>
/// Execute and manage the filter pane and quick filter control queries.
/// </summary>
internal void setPlanVersionForDataSources()
{
    element.updatePlanVersionRangesOnSavedViewChange();

    var planVersionRecId = reqTransPlanIdFilter.planVersion().RecId;

    if (planVersionRecId)
    {            
        ReqTransPoFormPlanVersionFilterManager::findEnabledRange(ReqPO_ds.queryBuildDataSource(), fieldNum(ReqPo, PlanVersion), true).value(queryValue(planVersionRecId));
        ReqTransPoFormPlanVersionFilterManager::findEnabledRange(ReqTrans_ds.queryBuildDataSource(), fieldNum(ReqTrans, PlanVersion), true).value(queryValue(planVersionRecId));
        ReqTransPoFormPlanVersionFilterManager::findEnabledRange(BatchReqTrans_ds.queryBuildDataSource(), fieldNum(ReqTrans, PlanVersion), true).value(queryValue(planVersionRecId));
        ReqTransPoFormPlanVersionFilterManager::findEnabledRange(ReqPlanVersion_ds.queryBuildDataSource(), fieldNum(ReqPlanVersion, ReqPlanId), true).value(reqTransPlanIdFilter.getReqPlanId());
        ReqTransPoFormPlanVersionFilterManager::findEnabledRange(ReqPlanVersion_ds.queryBuildDataSource(), fieldNum(ReqPlanVersion, RecId), true).value(queryValue(planVersionRecId));
        ReqTransPoFormPlanVersionFilterManager::findEnabledRange(ReqTransTransferIssue_ds.queryBuildDataSource(), fieldNum(ReqTrans, PlanVersion), true).value(queryValue(planVersionRecId));
    }
    else
    {
        Microsoft.Dynamics.ApplicationPlatform.XppServices.Instrumentation.XppRuntimeEventSource::EventWriteInfoLogMark(Exception::Warning, 'ReqTransPo does not have a plan version filter.');
    }
}

/// <summary>
/// If the saved view is changed, we need to adjust the PlanId and PlanVersion filters to the one selected in the view.
/// </summary>
private void updatePlanVersionRangesOnSavedViewChange()
{
    QueryBuildDataSource qbds = ReqPlanVersion_ds.queryRunQueryBuildDataSource();
    ReqPlanId planId;

    if (ReqTransPoFormRunConfigurationToggle::instance().isEnabled() && ReqTransPoFormRunConfigurationContext::instanceExists() && qbds)
    {
        if (ReqPlanVersion_ds.queryRun().query().dataSourceTable(tableNum(SysFilterTable)) && 
            qbds.findRange(fieldNum(SysFilterTable, RecId)))
        {
            RefRecId filterRecId = str2int64(qbds.findRange(fieldNum(SysFilterTable, RecId)).value());
            SysFilterTable filter = SysFilterTable::findFromId(filterRecId);
            planId = filter ? filter.FilterItemValueString[1] : '';
        }
        else
        {
            //qbds consists of several ranges for ReqPlanId, we need the not empty and enabled value if it exists
            QueryBuildRange planRange = ReqTransPoFormPlanVersionFilterManager::findEnabledRange(qbds, fieldNum(ReqPlanVersion, ReqPlanId));

            planId = planRange? planRange.value() : '';
        }

        if (planId)
        {
            PlanFilter.text(planId);
            PlanFilter.modified();

            RefRecId activePlanVersionRecId = reqTransPlanIdFilter.planVersion().RecId;
            QueryBuildRange planVersionRange = ReqTransPoFormPlanVersionFilterManager::findEnabledRange(qbds, fieldNum(ReqPlanVersion, RecId));

            if (planVersionRange)
            {
                RefRecId planVersionRecIdFromRange = str2Int64(planVersionRange.value());

                // when plan recid is changed, it should be changed in the saved query
                if (activePlanVersionRecId != planVersionRecIdFromRange)
                {
                    ReqTransPoFormPlanVersionFilterManager::updatePlanVersionQueryRanges(ReqPo_ds.queryRun().query(), activePlanVersionRecId);
                }
            }
        }
    }
}

// Used to save user selection in SysLastValue
public container pack()
{
    return [currentVersion, #CurrentList];
}

// Used to save user selection in SysLastValue
public boolean unpack(container packedClass)
{
    int version = RunBase::getVersion(packedClass);

    switch (version)
    {
        case currentVersion:
            [version, #CurrentList] = packedClass;
            return true;
    }
    return false;
}

