void enableFieldsActive()
{
    ReqSupplyPrinciple  supplyPrinciple;

    switch (reqTrans.RefType)
    {
        case ReqRefType::ItemPlannedOrder:
            this.groupPurchaseVisible(true);
            this.groupProductionVisible(false);
            this.groupTransferVisible(false);

            reqPo_VendId.allowEdit(true);
            break;

        case ReqRefType::PmfPlannedProdBatch:
        case ReqRefType::BOMPlannedOrder:
            this.groupPurchaseVisible(false);
            this.groupProductionVisible(true);
            this.groupTransferVisible(false);

            reqPo_VendId.allowEdit(false);
            break;

        case ReqRefType::TransferPlannedOrder:
            this.groupPurchaseVisible(false);
            this.groupProductionVisible(false);
            this.groupTransferVisible(true);

            reqPo_VendId.allowEdit(false);
            break;

        case ReqRefType::PlannedKanban:
            supplyPrinciple = reqPO.supplyPrinciple(reqTransForm.reqPlanData());

            this.groupPurchaseAllowEdit(supplyPrinciple == ReqSupplyPrinciple::Receipt);
            reqPo_VendId.allowEdit(supplyPrinciple == ReqSupplyPrinciple::Receipt);

            this.groupProductionAllowEdit(supplyPrinciple == ReqSupplyPrinciple::Conversion);

            // even if the supply principle is transfer the user should not change the from warehouse as this is controlled by the kanban rule
            this.groupTransferAllowEdit(false);
            break;
    }
}

