public void init()
{
    ReqParameters::checkPlanningProcessesAreEnabled();

    openedWithRecord = element.args() && (element.args().record() || element.args().lookupRecord());

    reqTransForm = ReqTransFormPO::newFormRun(element);

    if (!openedWithRecord)
    {
        xSysLastValue::getLast(this);
    }

    super();

    element.applyToggles();
    reqTransForm.parmUseCheckmarks(true);

    reqTransForm.formInitPost();

    element.initDesign();

    prodRecBasketFormInteraction = ProdRecBasketFormInteraction::newStandard(this);

    this.initPlanIdFilter();

    reqTransPlanIdFilter = ReqTransPlanIdFilter::newStandard(formControlStr(ReqTransPo, PlanFilter), this, planIdFilter);

    Product_inventUnitId.visible(FeatureStateProvider::isFeatureEnabled(ReqTransPoCreateWithPurchQtyFeature::instance()));
}

void initDesign()
{
    if (reqTransForm.parmReqRefType() != ReqRefType::None)
    {
        buttonKanbanRule.visible(false);
    }
    else
    {
        buttonKanbanRule.visible(true);
    }

    switch (reqTransForm.parmReqRefType())
    {
        case ReqRefType::ItemPlannedOrder:

            reqPo_VendId.visible(true);

            buttonReqTransDerived.visible(false);
            buttonReqRoute.visible(false);
            buttonReqRouteJobs.visible(false);

            element.design().caption("@SYS5151");
            headerTitle.text("@SYS5151");

            break;

        case ReqRefType::PmfPlannedProdBatch:

        case ReqRefType::BOMPlannedOrder:

            reqPo_VendId.visible(false);

            buttonReqTransDerived.visible(true);
            buttonReqRoute.visible(true);
            buttonReqRouteJobs.visible(true);

            element.design().caption("@SYS12742");
            headerTitle.text("@SYS12742");
            break;

        case ReqRefType::TransferPlannedOrder:

            reqPo_VendId.visible(false);

            buttonReqTransDerived.visible(false);
            buttonReqRoute.visible(false);
            buttonReqRouteJobs.visible(false);

            InventoryDimension_InventLocationId.label("@SYS8519");
            InventoryDimension_InventLocationId.helpText("@SYS54653");

            element.design().caption("@SYS58078");
            headerTitle.text("@SYS58078");
            break;
    }

    this.showPeggingFactBox(!tabPagePegging.isDisplayed());
}

