private void toggleDimensionFields(Set _fieldIds, NoYes _visible)
{
    InventDimParm reqTransInventDimParmEnabled = reqTransForm.parmInventDimParmEnabled();
    SetEnumerator enumerator = _fieldIds.getEnumerator();

    while (enumerator.moveNext())
    {
        FieldId dimFieldId = enumerator.current();
        FieldId dimParmFieldId = InventDim::dim2dimParm(dimFieldId);
        reqTransInventDimParmEnabled.(dimParmFieldId) = _visible;
    }

    reqTransForm.parmInventDimParmEnabled(reqTransInventDimParmEnabled);
}

private void initPlanIdFilter()
{
    if (!openedWithRecord)
    {
        if (reqTransForm is ReqTransFormPoPeriodFilter)
        {
            // In this case we want to take the value of the plan id that was provided by the caller
            // even if planIdFilter was set by the xSysLastValue::getLast(this); call.
            planIdFilter = reqTransForm.defaultReqPlanIdSched();
        }
        else if (planIdFilter == '') // The planIdFilter was not set by the xSysLastValue::getLast(this); call.
        {
            if (element.args().menuItemName() == menuitemDisplayStr(ReqPoGridViewPurchaseTodayOrOverdue))
            {
                planIdFilter = ReqPlanSched::defaultStaticId();
            }
            else
            {
                planIdFilter = ReqPlanSched::defaultDynamicId();
            }
        }
    }
    else
    {
        Args args = element.args();
        Common argsRecord = args.record() ? args.record() : args.lookupRecord();
        if (argsRecord && argsRecord.TableId == tablenum(ReqPO))
        {
            ReqPO argsReqPo = argsRecord;
            planIdFilter = ReqPlanVersion::find(argsReqPo.PlanVersion).ReqPlanId;
        }
        else if (argsRecord && argsRecord.TableId == tablenum(ReqTrans))
        {
            ReqTrans argsReqTrans = argsRecord;
            planIdFilter = ReqPlanVersion::find(argsReqTrans.PlanVersion).ReqPlanId;
        }
    }
}

