public void write()
{
    boolean changingDlvDate = false;
    date    dlvDate;

    reqTransForm.reqPoDSWritePre();

    if (ReqPO.RecId != 0 && ReqPO.ReqDateDlv != ReqPO.orig().ReqDateDlv)
    {
        changingDlvDate = true;
        dlvDate         = ReqPO.ReqDateDlv;
    }

    super();

    if (changingDlvDate && ReqPO.ReqDateDlv != dlvDate)
    {
        ReqPO.updateReqDateOnTheServer(dlvDate);
        ReqPO.reread();
        ReqPo_DS.reread();
        ReqPo_DS.refresh();
    }

    reqTransForm.reqPoDSWritePost();
}

