public int active()
{
    int ret;

    ret = super();

    reqTransForm.reqPoDSActivePost();

    if (#PmfEnabled)
    {
        if (reqPo.RefType == ReqRefType::PmfPlannedProdBatch)
        {
            productionPlannedOrder_ItemBomId.label("@PRO101");
        }
        else
        {
            productionPlannedOrder_ItemBomId.label("@SYS22298");
        }
        this.object(fieldnum(ReqPO,PmfPlanningItemId)).enabled(
        reqPo.RefType == ReqRefType::PmfPlannedProdBatch
        && InventTable::find(reqPo.ItemId).PmfProductType == PmfProductType::Co_Product);
    }

    element.pdsCWEnableFields();
    reqPo_ds.object(fieldnum(ReqPO, ReqPOStatus)).allowEdit(!reqTrans.IsDerivedDirectly);

    boolean isFromWarehouseEditable = element.isFromWarehouseEditable();
    inventDimTransferIssue_ds.allowEdit(isFromWarehouseEditable);
    reqTransTransferIssue_ds.allowEdit(isFromWarehouseEditable);

    // We are setting allowEdit directly on the form controls, because editing on them is disabled by the form controller.
    InventDimTransferIssue_InventLocationId.allowEdit(isFromWarehouseEditable);
    InventDimTransferIssue_InventLocationId_Details.allowEdit(isFromWarehouseEditable);

    return ret;
}

public void executeQuery()
{
    reqTransForm.reqPoDSExecuteQueryPre();

    // A direct filter by planVersion is used to ensure that SQL can pick a better plan of execution - it can use statistics for a given plan version instead of general statistics for all the versions
    element.setPlanVersionForDataSources();

    super();

    reqTransForm.reqPoDSExecuteQueryPost();
}

public void research(boolean _retainPosition = false)
{
    using (var instrumentation = this.createResearchInstrumentation())
    {
        ReqPO reqPOCursor;

        reqPOCursor.data(reqPo_ds.cursor());

        // do NOT retain position because of paging queries performance (up to ~10x slower comparing to non-paged query)
        super(false);

        if (_retainPosition && shouldRetainPosition)
        {
            // positionToRecord is not optimal for a large resultset because kernel has to execute paged queries when scrolling beyond cached set
            // findRecord is considered as obsolete API however has no paging overhead
            reqPo_ds.findRecord(reqPOCursor);
        }
    }
}

