public void create(boolean _append = false)
{
    ReqPO newReqPo;

    if (! reqTransForm.parmCreateByForm())
    {
        super(_append);
    }
    else
    {
        reqTransForm.parmReqPlanId(reqTransPlanIdFilter.getReqPlanId());
        newReqPo = reqTransForm.reqPoDSCreatePre();

        if (newReqPo.RecId)
        {
            super(_append);

            reqTransForm.reqPoDSCreatePost(newReqPo);
        }
    }
}

